import React, { Fragment, useState, useEffect } from 'react';
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { useDispatch, useSelector } from 'react-redux';
import { AdminUpdateProfileAction } from '../../Redux/Actions/AuthActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import Spinner from '../../Layouts/Spinner';

function Profile() {
    const dispatch = useDispatch<any>();
    const [profileImage, setProfileImage] = useState('');
    const formSchema = Yup.object().shape({
        full_name: Yup.string()
            .required(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.FULL_NAME_VALID)
            .max(25, VALIDATION_MESSAGE?.FULL_NAME_MAX),

        mobile_number: Yup.string()
            .required(VALIDATION_MESSAGE?.MOBILE_NUMBER_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(new RegExp('[0-9]{9}'), VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/, VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .min(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MIN)
            .max(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MAX),

        profile_picture: Yup.mixed()
            .test("fileType", VALIDATION_MESSAGE?.USER_UNSUPPORTED, (value) => {
                return value.length && ["image/jpeg", "image/png", "image/jpg"].includes(value[0].type)
            }),

    });
    const {
        register,
        handleSubmit,
        reset,
        setValue,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
        defaultValues: {
            profile_picture: "",
            full_name: "",
            country_code: 91,
            mobile_number: ""
        }
    });
    const selectFileHandler = (e: any) => {
        let file = e.target.files[0];
        setProfileImage(file);
    }
    const onSubmit = async (payload: any) => {
        delete payload['profile_picture'];
        if (profileImage != '') {
            payload['profile_picture'] = profileImage;
        }
        dispatch(AdminUpdateProfileAction(payload));
    };
    const { data_get_profile, status_get_profile, error_get_profile, loading_get_profile } = useSelector((state: any) => state.AdminGetProfileState);
    // if (status_get_profile) {
    //     setValue("full_name", data_get_profile?.full_name);
    //     setValue("country_code", data_get_profile?.country_code);
    //     setValue("mobile_number", data_get_profile?.mobile_number);
    // }
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    useEffect(() => {
        if (status_get_profile) {
            setValue("full_name", data_get_profile?.full_name);
            setValue("country_code", data_get_profile?.country_code);
            setValue("mobile_number", data_get_profile?.mobile_number);
        }
    }, [dispatch, data_get_profile]);
    return (
        <Fragment>
            {loading_get_profile ? <Spinner />:""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">                 
                    <div className="container">
                        <div className="panel-header bg-primary-gradient">
                            <div className="page-inner py-3">
                                <div className="d-flex align-items-left align-items-md-center flex-column flex-md-row">
                                    <div>
                                        <h2 className="text-white mb-0 fw-bold">Profile</h2>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <form onSubmit={handleSubmit(onSubmit)} className="card" encType="multipart/form-data">
                            <div className="p-4">
                                <div className="form-row">
                                    <div className="form-group col-md-6">
                                        <label htmlFor="name">Profile Picture</label>
                                        <div className="upload-image-back">
                                            <input
                                                type="file"
                                                accept="image/jpeg,image/png"
                                                className="form-control"
                                                {...register('profile_picture', {
                                                    onChange: (e) => { selectFileHandler(e) }
                                                })}
                                            />
                                            {/* <span className="placetext">Upload Image</span>
                                            <span className="img-icon"><i className="fa fa-image"></i></span> */}
                                        </div>
                                        <p className="text-danger">{errors?.profile_picture?.message ?? error_get_profile?.errors?.profile_picture?.msg}</p>
                                    </div>
                                    <div className="form-group col-md-6">
                                        <label htmlFor="name">Full Name</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="name"
                                            placeholder="Name"
                                            minLength={1}
                                            maxLength={25}
                                            {...register('full_name')}
                                        />
                                        <p className="text-danger">{errors?.full_name?.message ?? error_get_profile?.errors?.full_name?.msg}</p>
                                    </div>
                                    <div className="form-group col-md-6">
                                        <label htmlFor="mobile_number">Mobile Number</label>
                                        <input
                                            type="number"
                                            className="form-control"
                                            id="name"
                                            placeholder="Mobile Number"
                                            {...register('mobile_number', {
                                                onChange: (e) => {
                                                    if (e.target.value.length >= 10) {
                                                        setValue(`mobile_number`, e.target.value.slice(0, 10))
                                                    }
                                                    else {
                                                        setValue(`mobile_number`, e.target.value)
                                                    }
                                                }
                                            })}
                                        />
                                        <p className="text-danger">{errors?.mobile_number?.message ?? error_get_profile?.errors?.mobile_number?.msg}</p>
                                    </div>
                                </div>
                                <div className="mt-3">
                                    <button type="submit" className="btn btn-primary">Update</button>
                                </div>
                            </div>
                        </form>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}
export default Profile;
