import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2'
import { Link } from 'react-router-dom';

import { AdminGetUserAction, AdminUpdateUserStatusAction, AdminDeleteUserAction, AdminGetByIdUserAction } from '../../../Redux/Actions/UserActions';
import { REACT_APP_BASE_URL, DISTRIBUTO_ROLE_ID, REACT_PAGINATE_LIMIT, REACT_PAGINATE_NEXT_LABEL, REACT_PAGINATE_PREVIOUS_LABEL, REACT_PAGINATE_BREAK_LABEL } from '../../../Config/Config';
import ReactPaginate from 'react-paginate';
import { toast } from 'react-toastify';
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";

import { statusOption, fillterstatusOption, encodeID, getDate } from "../../../Utils/Helper";
import { VALIDATION_MESSAGE } from '../../../Constants/Constants';

import ImageHelper from '../../../Utils/imageHelper';

import Header from '../../../Layouts/Header';
import Sidebar from '../../../Layouts/Sidebar';
import Footer from '../../../Layouts/Footer';
import Spinner from '../../../Layouts/Spinner';
const Distributor = () => {
    const dispatch = useDispatch<any>();
    const navigate = useNavigate();
    const [paginateLimit, setPaginateLimit] = useState(REACT_PAGINATE_LIMIT);
    const [paginatePage, setPaginatePage] = useState(1);
    const [search, setSearch] = useState('');
    const [searchStatus, setSearchStatus] = useState(404);
    const [paginateCount, setPaginateCount] = useState(0);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const { status_admin_get_user, data_admin_get_user, loading_admin_get_user } = useSelector((state: any) => state.AdminGetUserState);
    const { status_admin_update_user_status, data_admin_update_user_status, loading_admin_update_user_status } = useSelector((state: any) => state.AdminUpdateUserStatusState);
    const { status_admin_delete_user, data_admin_delete_user, loading_admin_delete_user } = useSelector((state: any) => state.AdminDeleteUserState);

    const {
        control,
    } = useForm({
        defaultValues: {
            status: 2
        }
    });

    const handlePaginateClick = (event: any) => {
        var next_page_set = event.selected + 1;
        setPaginatePage(next_page_set);
        const params = {
            "role_id": DISTRIBUTO_ROLE_ID,
            "search": search,
            "status": searchStatus,
            "page": next_page_set,
            "limit": paginateLimit
        }
        dispatch(AdminGetUserAction(params));
    };

    const confirmStatusChange = async (id: number, status: number) => {
        var text_message = '';
        if (status === 0) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USERS_DEACTIVATE;
        }
        else if (status === 1) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_ACTIVATE;
        }
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: text_message,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                statusChange(id, status);
            }
        });
    };

    const statusChange = async (id: number, status: number) => {
        const params = {
            "user_id": id,
            "user_status": status
        }
        dispatch(AdminUpdateUserStatusAction(params));
    };

    const confirmDelete = async (delete_id: number) => {
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_DELETE,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                deleteData(delete_id);
            }
        });
    };

    const deleteData = async (delete_id: number) => {
        const params = {
            "delete_id": delete_id
        }
        dispatch(AdminDeleteUserAction(params));
    };

    const editData = async (user_id: number) => {
        dispatch(AdminGetByIdUserAction('RESET'));
        navigate(REACT_APP_BASE_URL + 'distributor-edit/' + encodeID(user_id), { replace: true })
    };

    const viewData = async (user_id: number) => {
        dispatch(AdminGetByIdUserAction('RESET'));
        navigate(REACT_APP_BASE_URL + 'distributor-view/' + encodeID(user_id), { replace: true })
    };

    const handleSearch = (event: any) => {
        if (event.keyCode === 13) {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "role_id": DISTRIBUTO_ROLE_ID,
                "search": event.target.value,
                "status": searchStatus,
                // "page": paginatePage,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetUserAction(params));
        }
        else if (event.target.value === '') {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "role_id": DISTRIBUTO_ROLE_ID,
                "search": event.target.value,
                "status": searchStatus,
                // "page": paginatePage,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetUserAction(params));
        }
    }
    const statusChangeFilltr = (status: any) => {
        // if (status !== '') {
            setPaginatePage(1);
            setSearchStatus(status);
            const params = {
                "role_id": DISTRIBUTO_ROLE_ID,
                "search": search,
                "status": status,
                // "page": paginatePage,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetUserAction(params));
        // }
    }


    useEffect(() => {
        const params = {
            "role_id": DISTRIBUTO_ROLE_ID,
            "search": search,
            "status": searchStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        dispatch(AdminGetUserAction(params));
    }, []);

    useEffect(() => {
        const params = {
            "role_id": DISTRIBUTO_ROLE_ID,
            "search": search,
            "status": searchStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        if (status_admin_update_user_status) {
            dispatch(AdminGetUserAction(params));
            toast.success(data_admin_update_user_status?.message);
            dispatch(AdminUpdateUserStatusAction('RESET'));
        }
        if (status_admin_delete_user) {
            dispatch(AdminGetUserAction(params));
            dispatch(AdminDeleteUserAction('RESET'));
            toast.success(data_admin_delete_user?.message);
        }
        // if (data_admin_get_user?.data?.rows?.length > 0) {
            setPaginateCount(Math.ceil(data_admin_get_user?.data?.count / paginateLimit));
        // }
    }, [data_admin_get_user, status_admin_update_user_status, status_admin_delete_user]);
    return (
        <Fragment>
            {loading_admin_get_user ? <Spinner />:""}
            {loading_admin_update_user_status ? <Spinner />:""}
            {loading_admin_delete_user ? <Spinner />:""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Distributor Management</h4>
                                {/* <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "distributor-create"}>
                                            Create Distributor
                                        </Link>
                                    </li>
                                </ul> */}
                            </div>
                            <div className="row align-items-center mb-3">
                                <div className="col">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="row">
                                            <div className="col-lg-4 col-md-4 pl-0">
                                                <div className="input-group">
                                                    <input type="text" className="form-control" onKeyDown={handleSearch} onChange={handleSearch} placeholder="Search ..." />
                                                </div>
                                            </div>
                                            <div className="col-lg-2 col-md-2">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Status"
                                                            // value={
                                                            //     statusOption().filter(option => option.value === searchStatus)
                                                            // }
                                                            onChange={(e: any) => {
                                                                statusChangeFilltr(e?.value);
                                                            }}
                                                            options={fillterstatusOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-auto">
                                    <Link to={REACT_APP_BASE_URL + "distributor-create"} className="btn btn-primary ml-2">
                                        Create Distributor
                                    </Link>
                                </div>
                            </div>
                            <div className="card listing_c order_managment card-list">
                                <div className="row align-items-center">
                                    <div className="col-lg-1 col-md-1">
                                        <h6 className="fw-bold mb-1">S No</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2">
                                        <h6 className="fw-bold mb-1">Name</h6>
                                    </div>
                                    <div className="col-lg-3 col-md-3">
                                        <h6 className="fw-bold mb-1">Email ID</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2">
                                        <h6 className="fw-bold mb-1">Registration date</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Status</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Action</h6>
                                    </div>
                                </div>
                                {data_admin_get_user?.data?.rows?.length > 0 ?
                                    data_admin_get_user?.data?.rows.map((d: any, i: any) => {
                                        var get_status = statusOption().filter(option => option.value === d?.status);
                                        return (
                                            <div className="row align-items-center" key={i}>
                                                <div className="col-lg-1 col-md-1  text-center">
                                                    <div className="pro_name">
                                                        {/* <img src={String(ImageHelper.AvatarImage)} alt="..." className="avatar-img rounded-circle" /> */}
                                                        {(paginatePage - 1) * paginateLimit + i + 1}
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.full_name}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-3 col-md-3">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.email}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {getDate(d?.createdAt)}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="pro_name">
                                                        {get_status[0]?.label}
                                                    </div>
                                                    {/* <Controller
                                                        control={control}
                                                        name={"status"}
                                                        render={(props) => (
                                                            <Select
                                                                placeholder="Status"
                                                                value={
                                                                    statusOption().filter(option => option.value === d?.status)
                                                                }
                                                                onChange={(e: any) => {
                                                                    confirmStatusChange(d?.id, e?.value);
                                                                }}
                                                                options={statusOption()}
                                                                classNamePrefix="dropDown"
                                                                isMulti={false}
                                                                className="react-select"
                                                            />
                                                        )}
                                                    /> */}
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="list_right">
                                                        {/* <span onClick={() => { editData(d?.id); }}>
                                                            <i className="fas fa-edit" title="Edit"></i>
                                                        </span> */}
                                                        <span onClick={() => { confirmDelete(d?.id); }}>
                                                            <i className="fas fa-trash-alt" title="Delete"></i>
                                                        </span>
                                                        <span onClick={() => { viewData(d?.id); }}>
                                                            <i className="fas fa-eye" title="View"></i>
                                                        </span>
                                                        {(() => {
                                                            if (d?.status === 1) {
                                                                if (d?.user_status === 1) {
                                                                    return (
                                                                        <span onClick={() => { confirmStatusChange(d?.id,0); }}>
                                                                            <i className="fas fa-ban" title="De-Activate"></i>
                                                                        </span>
                                                                    )
                                                                } else if (d?.user_status === 0) {
                                                                    return (
                                                                        <span onClick={() => { confirmStatusChange(d?.id,1); }}>
                                                                            <i className="fas fa-check" title="Activate"></i>
                                                                        </span>
                                                                    )
                                                                }
                                                            }
                                                        })()}
                                                        {/* <span onClick={() => { confirmStatusChange(d?.id,0); }}>
                                                            <i className="fas fa-ban" title="De-Activate"></i>
                                                        </span>
                                                        <span onClick={() => { confirmStatusChange(d?.id,1); }}>
                                                            <i className="fas fa-check" title="Activate"></i>
                                                        </span> */}
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                                    : "No data found."}
                            </div>
                            {paginateCount > 0 ?
                                <div className="col-sm-12 react-paginate cutome-react-paginate justify-content-center">
                                    <ReactPaginate
                                        className="pagination justify-content-center"
                                        breakLabel={REACT_PAGINATE_BREAK_LABEL}
                                        nextLabel={REACT_PAGINATE_NEXT_LABEL}
                                        previousLabel={REACT_PAGINATE_PREVIOUS_LABEL}
                                        pageRangeDisplayed={paginateLimit}
                                        onPageChange={handlePaginateClick}
                                        pageCount={paginateCount}
                                        breakClassName={'page-item'}
                                        breakLinkClassName={'page-link'}
                                        containerClassName={'pagination'}
                                        pageClassName={'page-item'}
                                        pageLinkClassName={'page-link'}
                                        previousClassName={'page-item'}
                                        previousLinkClassName={'page-link'}
                                        nextClassName={'page-item'}
                                        nextLinkClassName={'page-link'}
                                        activeClassName={'active'}
                                    />
                                </div>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default Distributor;
