import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';
const Category = () => {
    const dispatch = useDispatch<any>();
    const navigate = useNavigate();
    const [paginateLimit, setPaginateLimit] = useState(REACT_PAGINATE_LIMIT);
    const [paginatePage, setPaginatePage] = useState(1);
    const [search, setSearch] = useState('');
    const [categoryStatus, setCategoryStatus] = useState(404);
    const [categoryParentId, setCategoryParentId] = useState(404);
    const [getParentCategory, setParentCategory] = useState<any[]>([]);
    const [paginateCount, setPaginateCount] = useState(0);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_get_parent_category, data_admin_get_parent_category, error_admin_get_parent_category, loading_admin_get_parent_category } = useSelector((state: any) => state.AdminGetParentCategoryState);
    const { status_admin_get_category, data_admin_get_category, error_admin_get_category, loading_admin_get_category } = useSelector((state: any) => state.AdminGetCategoryState);
    const { status_admin_change_status_category, data_admin_change_status_category, error_admin_change_status_category, loading_admin_change_status_category } = useSelector((state: any) => state.AdminChangeStatusCategoryState);
    const { status_admin_delete_category, data_admin_delete_category, error_admin_delete_category, loading_admin_delete_category } = useSelector((state: any) => state.AdminDeleteCategoryState);

    const {
        control,
    } = useForm({});

    const confirmStatusChange = async(id: number, status: number) => {
      var text_message = "";
      if(status === 0) {
        text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_CATEGORY_DISABLE;
      }else if (status === 1) {
        text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_CATEGORY_ENABLE;
      }
      Swal.fire({
        title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
        text: text_message,
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#31CE36',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Ok'
    }).then((result) => {
        if (result.isConfirmed) {
            statusChange(id, status);
        }
    });
    } 

    const statusChange = (id: number, status: number) => {
      const params = {
        "category_id": id,
        "status" : status
      }
      dispatch(AdminChangeStatusCategoryAction(params));
      dispatch(AdminGetParentCategoryAction({}));
    }

    const confirmDelete = async(delete_id: number) => {
      Swal.fire({
        title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
        text: VALIDATION_MESSAGE?.ARE_YOU_DELETE_CATEGORY,
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#31CE36',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Ok'
    }).then((result) => {
        if (result.isConfirmed) {
            deleteData(delete_id);
        }
    });
    }

    const deleteData = (delete_id: number) => {
      const params = {
        "delete_id": delete_id
      }
      dispatch(AdminDeleteCategoryAction(params))
    }

    const editData = async(user_id: number) => {
      dispatch(AdminGetByIdCategoryAction('RESET'));
      navigate(REACT_APP_BASE_URL + "category-edit/" + encodeID(user_id), { replace: true })
    }

    const handlePaginateClick = (event: any) => {
        var next_page_set = event.selected + 1;
        setPaginatePage(next_page_set);
        const params = {
            "search": search,
            "parentId": categoryParentId,
            "status": categoryStatus,
            "page": next_page_set,
            "limit": paginateLimit
        }
        dispatch(AdminGetCategoryAction(params));
    };

    const handleSearch = (event: any) => {
        if (event.keyCode === 13) {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "parentId": categoryParentId,
                "status": categoryStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetCategoryAction(params));
        }
        else if (event.target.value === '') {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "parentId": categoryParentId,
                "status": categoryStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetCategoryAction(params));
        }
    }

    const categoryParentIdFilltr = (parentId: any) => {
        setPaginatePage(1);
        setCategoryParentId(parentId);
        const params = {
            "search": search,
            "parentId": parentId,
            "status": categoryStatus,
            "page": 1,
            "limit": paginateLimit
        }
        dispatch(AdminGetCategoryAction(params));
    }

    const categoryStatusFilltr = (status: any) => {
        setPaginatePage(1);
        setCategoryStatus(status);
        const params = {
            "search": search,
            "parentId": categoryParentId,
            "status": status,
            "page": 1,
            "limit": paginateLimit
        }
        dispatch(AdminGetCategoryAction(params));
    }

    useEffect(() => {
      dispatch(AdminGetParentCategoryAction({}))
      const params = {
        "search": search,
        "parentId": categoryParentId,
        "status": categoryStatus,
        "page": paginatePage,
        "limit": paginateLimit
      }
      dispatch(AdminGetCategoryAction(params))
    },[])

    useEffect(() => {

    })


    return (
        <Fragment>
            {loading_admin_get_category ? <Spinner /> : ""}
            {loading_admin_change_status_category ? <Spinner /> : ""}
            {loading_admin_delete_category ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Category Management</h4>
                            </div>
                            <div className="row align-items-center mb-3">
                                <div className="col">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="row">
                                            <div className="col-lg-4 col-md-4 pl-0">
                                                <div className="input-group">
                                                    <input type="text" className="form-control" onKeyDown={handleSearch} onChange={handleSearch} placeholder="Search ..." />
                                                </div>
                                            </div>
                                            <div className="col-lg-4 col-md-4">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Select Parent Category"
                                                            onChange={(e: any) => {
                                                                categoryParentIdFilltr(e?.value);
                                                            }}
                                                            options={getParentCategory}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Category Status"
                                                            onChange={(e: any) => {
                                                                categoryStatusFilltr(e?.value);
                                                            }}
                                                            options={categoryStatusOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-auto">
                                    <Link to={REACT_APP_BASE_URL + "category-create"} className="btn btn-primary ml-2">
                                        Create Category
                                    </Link>
                                </div>
                            </div>
                            <div className="card listing_c order_managment card-list">
                                <div className="row align-items-center">
                                    <div className="col-lg-1 col-md-1">
                                        <h6 className="fw-bold mb-1">Serial No</h6>
                                    </div>
                                    <div className="col-lg-7 col-md-7">
                                        <h6 className="fw-bold mb-1">Name of the category</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Status</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Action</h6>
                                    </div>
                                </div>
                                {data_admin_get_category?.data?.rows?.length > 0 ?
                                    data_admin_get_category?.data?.rows.map((d: any, i: any) => {
                                        var get_status = categoryStatusOption().filter(option => option.value === d?.status);
                                        return (
                                            <div className="row align-items-center" key={i}>
                                                <div className="col-lg-1 col-md-1  text-center">
                                                    <div className="pro_name">
                                                    {(paginatePage -1) * paginateLimit + i + 1 }
                                                    </div>
                                                </div>
                                                <div className="col-lg-7 col-md-7">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                           {d.name}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="pro_name">
                                                       {get_status[0]?.label}
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="list_right">
                                                        <span className="span-fa-edit" onClick={() => { editData(d?.id); }}>
                                                            <i className="fas fa-edit" title="Edit"></i>
                                                        </span>

                                                        {(() => {
                                                            if (d?.parentId !== 0) {
                                                                return (
                                                                    <span className="span-fa-trash" onClick={() => { confirmDelete(d?.id);}}>
                                                                        <i className="fas fa-trash-alt" title="Delete"></i>
                                                                    </span>
                                                                )
                                                            }
                                                        })()}
                                                        {(() => {
                                                            if (d?.status === 1) {
                                                                return (
                                                                  <span className="span-fa-disable" onClick={() => { confirmStatusChange(d?.id, 0); }}>
                                                                        <i className="fas fa-ban" title="Disable"></i>
                                                                    </span>
                                                                )
                                                            } else if (d?.status === 0) {
                                                                return (
                                                                    <span className="span-fa-enable" onClick={() => { confirmStatusChange(d?.id, 1); }}>
                                                                        <i className="fas fa-check" title="Enable"></i>
                                                                    </span>
                                                                )
                                                            }
                                                        })()}
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                                 : "" }
                            </div>
                            {paginateCount > 0 ?
                                <div className="col-sm-12 react-paginate cutome-react-paginate justify-content-center">
                                    <ReactPaginate
                                        className="pagination justify-content-center"
                                        breakLabel={REACT_PAGINATE_BREAK_LABEL}
                                        nextLabel={REACT_PAGINATE_NEXT_LABEL}
                                        previousLabel={REACT_PAGINATE_PREVIOUS_LABEL}
                                        pageRangeDisplayed={paginateLimit}
                                        onPageChange={handlePaginateClick}
                                        pageCount={paginateCount}
                                        breakClassName={'page-item'}
                                        breakLinkClassName={'page-link'}
                                        containerClassName={'pagination'}
                                        pageClassName={'page-item'}
                                        pageLinkClassName={'page-link'}
                                        previousClassName={'page-item'}
                                        previousLinkClassName={'page-link'}
                                        nextClassName={'page-item'}
                                        nextLinkClassName={'page-link'}
                                        activeClassName={'active'}
                                    />
                                </div>
                                : "No data found"}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment >
    );
}

export default Category;
