import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import { Link } from 'react-router-dom';
import { AdminAddUserAction } from '../../../Redux/Actions/UserActions';
import { VALIDATION_MESSAGE } from '../../../Constants/Constants';
// import { statusOption } from "../../../Utils/Helper";
// import { REACT_APP_BASE_URL } from '../../../Config/Config';

import { useForm } from 'react-hook-form';
// import { useForm, Controller } from 'react-hook-form';
// import Select from "react-select";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

import { toast } from 'react-toastify';

import Header from '../../../Layouts/Header';
import Sidebar from '../../../Layouts/Sidebar';
import Footer from '../../../Layouts/Footer';
import Spinner from '../../../Layouts/Spinner';
import { getIpAddress } from "../../../Utils/Helper";
const AddDistributorForm = () => {
    const dispatch = useDispatch<any>();

    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const { status_admin_add_user, data_admin_add_user, error_admin_add_user, loading_admin_add_user } = useSelector((state: any) => state.AdminAddUserState);

    const [passwordType, setPasswordType] = useState(false);
    const [confirmPasswordType, setConfirmPasswordType] = useState(false);

    const [drugLicenseFile, setDrugLicenseFile] = useState('');

    const formSchema = Yup.object().shape({
        full_name: Yup.string()
            .required(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.FULL_NAME_VALID)
            .max(25, VALIDATION_MESSAGE?.FULL_NAME_MAX),

        company_name: Yup.string()
            .required(VALIDATION_MESSAGE?.COMPANY_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.COMPANY_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.COMPANY_NAME_VALID)
            .max(25, VALIDATION_MESSAGE?.COMPANY_NAME_MAX),

        email: Yup.string()
            .required(VALIDATION_MESSAGE?.EMAIL_REQUIRED)
            .email(VALIDATION_MESSAGE?.VALID_EMAIL)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),

        mobile_number: Yup.string()
            .required(VALIDATION_MESSAGE?.MOBILE_NUMBER_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(new RegExp('[0-9]{9}'), VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/, VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .min(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MIN)
            .max(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MAX),

        pan_card_number: Yup.string()
            .required(VALIDATION_MESSAGE?.PAN_CARD_NUMBER_REQUIRED)
            .min(10, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID)
            .max(10, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(/^([a-zA-Z]([a-zA-Z]([a-zA-Z]([a-zA-Z]([a-zA-Z]([0-9]([0-9]([0-9]([0-9]([a-zA-Z])?)?)?)?)?)?)?)?)?)?$/, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID),

        gst_number: Yup.string()
            .required(VALIDATION_MESSAGE?.GST_NUMBER_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            // .matches(new RegExp('^[0-9]{2}[a-zA-Z]{5}[0-9]{4}[a-zA-Z]{1}[1-9a-zA-Z]{1}Z[0-9a-zA-Z]{1}$'), VALIDATION_MESSAGE?.GST_NUMBER_VALID),
            .matches(new RegExp('^[0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9]{1}Z[0-9A-Z]{1}$'), VALIDATION_MESSAGE?.GST_NUMBER_VALID),

        drug_license: Yup.mixed()
            .test("fileType", VALIDATION_MESSAGE?.DOCUMEMNT_UNSUPPORTED, (value) => {
                var file_type = value.length && ["image/jpeg", "image/png", "image/jpg", "application/pdf"].includes(value[0].type)
                if (file_type) {
                    return file_type;
                }
                else {
                    setValue('drug_license','');
                    return file_type;
                }     
            }),

            // password: Yup.string()
        //     .required(VALIDATION_MESSAGE?.PASSWORD_REQUIRED)
        //     .min(8, VALIDATION_MESSAGE?.PASSWORD_MIN_CHAR)
        //     .max(16, VALIDATION_MESSAGE?.PASSWORD_MAX_CHAR)
        //     .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
        //     .matches(/^(?=.*[A-Z])/, VALIDATION_MESSAGE?.PASSWORD_UPPER_CASE)
        //     .matches(/^(?=.*[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
        //     .matches(/^(?=.{6,20}$)\D*\d/, VALIDATION_MESSAGE?.PASSWORD_NUMBER_CASE),

        // confirm_password: Yup.string()
        //     .required(VALIDATION_MESSAGE?.CONFIRM_PASSWORD_REQUIRED)
        //     .min(8, VALIDATION_MESSAGE?.CONFIRM_PASSWORD_MIN_CHAR)
        //     .max(16, VALIDATION_MESSAGE?.CONFIRM_PASSWORD_MAX_CHAR)
        //     .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
        //     .matches(/^(?=.*[A-Z])/, VALIDATION_MESSAGE?.PASSWORD_UPPER_CASE)
        //     .matches(/^(?=.*[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
        //     .matches(/^(?=.{6,20}$)\D*\d/, VALIDATION_MESSAGE?.PASSWORD_NUMBER_CASE)
        //     .oneOf([Yup.ref('password')], VALIDATION_MESSAGE?.PASSWORD_CPWD_NOT_MATCHED)
    });

    const {
        register,
        handleSubmit,
        reset,
        setValue,
        getValues,
        formState: { errors },
        control,
    } = useForm({
        resolver: yupResolver(formSchema),
        defaultValues: {
            role_id: 3,
            full_name: "",
            company_name: "",
            email: "",
            country_code: 91,
            mobile_number: "",
            pan_card_number: "",
            // is_pan_card_number_verified: false,
            gst_number: "",
            // is_gst_number_verified: false,
            drug_license: "",
            // is_drug_license_verified: false,
            // password: "",
            // confirm_password: "",
            // status: 0
        }
    });
    const onSubmit = async (payload: any) => {
        delete payload['drug_license'];
        if (drugLicenseFile != '') {
            payload['drug_license'] = drugLicenseFile;
        }
        // if (payload['is_pan_card_number_verified'] === true) {
        //     payload['is_pan_card_number_verified'] = 1;
        // }
        // else {
        //     payload['is_pan_card_number_verified'] = 0;
        // }
        // if (payload['is_gst_number_verified'] === true) {
        //     payload['is_gst_number_verified'] = 1;
        // }
        // else {
        //     payload['is_gst_number_verified'] = 0;
        // }
        // if (payload['is_drug_license_verified'] === true) {
        //     payload['is_drug_license_verified'] = 1;
        // }
        // else {
        //     payload['is_drug_license_verified'] = 0;
        // }
        payload['device_type'] = 'web';
        payload['device_token'] = await getIpAddress();
        dispatch(AdminAddUserAction(payload));
    };
    const selectFileHandler = (e: any) => {
        let file = e.target.files[0];
        if (file !== '') {
            setDrugLicenseFile(file);
        }
    }
    useEffect(() => {
        if (status_admin_add_user) {
            toast.success(data_admin_add_user?.message);
            dispatch(AdminAddUserAction('RESET'));
            reset();
        }
    }, [dispatch, status_admin_add_user]);
    return (
        <Fragment>
            {loading_admin_add_user ? <Spinner />:""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Add Distributor</h4>
                                {/* <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "distributor"}>
                                            Distributor List
                                        </Link>
                                    </li>
                                </ul> */}
                            </div>
                            <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                <div className="p-4">
                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Full Name *</label>
                                            <input
                                                type="text"
                                                placeholder="Enter Full Name"
                                                className="form-control"
                                                minLength={1}
                                                maxLength={25}
                                                {...register('full_name')}
                                            />
                                            <p className="text-danger">{errors?.full_name?.message ?? error_admin_add_user?.errors?.full_name?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Company Name</label>
                                            <input
                                                type="text"
                                                placeholder="Enter Company Name"
                                                className="form-control"
                                                minLength={1}
                                                maxLength={25}
                                                {...register('company_name')}
                                            />
                                            <p className="text-danger">{errors?.company_name?.message ?? error_admin_add_user?.errors?.company_name?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Email *</label>
                                            <input
                                                type="email"
                                                placeholder="Email *"
                                                className="form-control"
                                                {...register("email")}
                                            />
                                            <p className="text-danger">{errors?.email?.message ?? error_admin_add_user?.errors?.email?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6 countryNumber">
                                            <label className="form-label">Mobile Number *</label>
                                            <span className="countryCode">+91</span>
                                            <input
                                                type="number"
                                                placeholder="Enter Mobile Number"
                                                className="form-control"
                                                {...register('mobile_number', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 10) {
                                                            setValue(`mobile_number`, e.target.value.slice(0, 10))
                                                        }
                                                        else {
                                                            setValue(`mobile_number`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.mobile_number?.message ?? error_admin_add_user?.errors?.mobile_number?.msg}</p>
                                        </div>
                                        {/* <div className="form-group col-md-6 password_check">
                                            <label className="form-label">Password *</label>
                                            <input
                                                type={passwordType ? "text" : "password"}
                                                placeholder="Enter Password"
                                                className="form-control"
                                                {...register('password')}
                                            />
                                            <span className={`${passwordType ? "fas fa-eye-slash" : "fas fa-eye"}`} onClick={() => { setPasswordType(!passwordType); }}></span>
                                            <p className="text-danger">{errors?.password?.message ?? error_admin_add_user?.errors?.password?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6 password_check">
                                            <label className="form-label">Confirm Password *</label>
                                            <input
                                                type={confirmPasswordType ? "text" : "password"}
                                                placeholder="Re-enter Password"
                                                className="form-control"
                                                {...register('confirm_password')}
                                            />
                                            <span className={`${confirmPasswordType ? "fas fa-eye-slash" : "fas fa-eye"}`} onClick={() => { setConfirmPasswordType(!confirmPasswordType); }}></span>
                                            <p className="text-danger">{errors?.confirm_password?.message ?? error_admin_add_user?.errors?.confirm_password?.msg}</p>
                                        </div> */}
                                        <div className="form-group col-md-6 form-group-checkbox">
                                            <label className="form-label">Pan Card Number *</label>
                                            {/* <span className="form-group-checkbox-span">
                                                <input
                                                    type="checkbox"
                                                    className="form-check-input"
                                                    {...register('is_pan_card_number_verified')}
                                                /> Verified
                                            </span> */}
                                            <input
                                                type="text"
                                                placeholder="Enter Pan Card Number"
                                                className="form-control uppercase_input"
                                                minLength={10}
                                                maxLength={10}
                                                {...register('pan_card_number', {
                                                    onChange: (e) => setValue(`pan_card_number`, e?.target?.value.toUpperCase())
                                                })}
                                            />
                                            <p className="text-danger">{errors?.pan_card_number?.message ?? error_admin_add_user?.errors?.pan_card_number?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6 form-group-checkbox">
                                            <label className="form-label">GST Number *</label>
                                            {/* <span className="form-group-checkbox-span">
                                                <input
                                                    type="checkbox"
                                                    className="form-check-input"
                                                    {...register('is_gst_number_verified')}
                                                /> Verified
                                            </span> */}
                                            <input
                                                type="text"
                                                placeholder="Enter GST Number"
                                                className="form-control uppercase_input"
                                                minLength={15}
                                                maxLength={15}
                                                {...register('gst_number', {
                                                    onChange: (e) => setValue(`gst_number`, e?.target?.value.toUpperCase())
                                                })}
                                            />
                                            <p className="text-danger">{errors?.gst_number?.message ?? error_admin_add_user?.errors?.gst_number?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6 form-group-checkbox">
                                            <label className="form-label">Drug License</label>
                                            {/* <span className="form-group-checkbox-span">
                                                <input
                                                    type="checkbox"
                                                    className="form-check-input"
                                                    {...register('is_drug_license_verified')}
                                                /> Verified
                                            </span> */}
                                            <input
                                                type="file"
                                                accept="image/jpeg,image/png,application/pdf"
                                                className="form-control"
                                                {...register('drug_license', {
                                                    onChange: (e) => { selectFileHandler(e) }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.drug_license?.message ?? error_admin_add_user?.errors?.drug_license?.msg}</p>
                                        </div>
                                        {/* <div className="form-group col-md-6">
                                            <label className="form-label">Status *</label>
                                            <Controller
                                                control={control}
                                                rules={{ required: VALIDATION_MESSAGE?.STATUS_REQUIRED }}
                                                name={"status"}
                                                // defaultValue={""}
                                                render={(props) => (
                                                    <Select
                                                        placeholder="Select Status"
                                                        value={
                                                            statusOption().filter(option => option.value === getValues('status'))
                                                        }
                                                        onChange={(e: any) => {
                                                            setValue(`status`, e?.value);
                                                        }}
                                                        options={statusOption()}
                                                        classNamePrefix="dropDown"
                                                        isMulti={false}
                                                        className="react-select"
                                                    />
                                                )}
                                            />
                                            <p className="text-danger">{errors?.status?.message ?? error_admin_add_user?.errors?.status?.msg}</p>
                                        </div> */}
                                    </div>
                                    <div className="mt-3 text-center">
                                        <button type="submit" className="btn btn-primary">Create</button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default AddDistributorForm;
