import React,{ useState } from "react";
import { ToastContainer, toast } from 'react-toastify';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import { useForm } from 'react-hook-form';

const Login = () => {

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        defaultValues: {
            email: "",
            password: ""
        }
    });

    const [passwordType, setPasswordType] = useState(false);
    return (
        <>

            <section className="login">
                <div className="wrapper wrapper-login">
                    <div className="container container-login animated fadeIn">
                        <h3 className="text-center">Sign In To Admin</h3>
                        <div className="login-form">
                            <form>
                            <div className="form-group form-floating-label">
                            <input
                                        type="email"
                                        placeholder="Enter Email Address"
                                        className="form-control input-border-bottom"

                                    />
                                    <p className="text-danger"></p>
                            </div>
                            <div className="form-group form-floating-label">
                                    <div className="position-relative">
                                        <div className="show-password">
                                            <span className={`${passwordType ? "fas fa-eye-slash" : "fas fa-eye"}`} ></span>
                                        </div>
                                        <input
                                            type= "password"
                                            placeholder="Enter Password"
                                            className="form-control input-border-bottom"

                                        />
                                    </div>
                                    <p className="text-danger"></p>
                                </div>
                                <div className="form-action mb-3">
                                    <button type="submit" className="btn btn-primary btn-rounded btn-login">Sign In</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </section>
            <ToastContainer
                autoClose={500}
                hideProgressBar={true}
                className="toast-immunoshop"
                position="bottom-center"
                newestOnTop={false}
                closeOnClick={true}
                rtl={false}
                pauseOnFocusLoss={false}
                draggable={false}
                pauseOnHover={false}
            />

        </>
    )
}

export default Login;
