import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AdminGetByIdUserAction, AdminChangeStatusUserAction } from '../../../Redux/Actions/UserActions';
import { useLocation } from 'react-router-dom';
import { statusOption, viewStatusOption, getLastURL, decodeID, getDate } from "../../../Utils/Helper";
import { Link } from 'react-router-dom';
import { REACT_APP_BASE_URL } from '../../../Config/Config';
import { useForm, Controller } from 'react-hook-form';
import Swal from 'sweetalert2'
import { VALIDATION_MESSAGE } from '../../../Constants/Constants';
import Select from "react-select";
import Header from '../../../Layouts/Header';
import Sidebar from '../../../Layouts/Sidebar';
import Footer from '../../../Layouts/Footer';
import Spinner from '../../../Layouts/Spinner';
const ViewBuyer = () => {
    const dispatch = useDispatch<any>();
    const location = useLocation();
    const viewId = getLastURL(location);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const { status_admin_get_by_id_user, data_admin_get_by_id_user, loading_admin_get_by_id_user } = useSelector((state: any) => state.AdminGetByIdUserState);
    const { status_admin_change_status_user, data_admin_change_status_user, loading_admin_change_status_user } = useSelector((state: any) => state.AdminChangeStatusUserState);
    const {
        register,
        control,
        setValue,
        getValues,
    } = useForm({
        defaultValues: {
            status: 2,
            is_pan_card_number_verified: 2,
        }
    });

    const confirmStatusChange = async (id: number, status: number) => {
        var pan_card_number_verified = data_admin_get_by_id_user?.data?.is_pan_card_number_verified;
        if (data_admin_get_by_id_user?.data?.is_pan_card_number_verified !== 1 && data_admin_get_by_id_user?.data?.is_pan_card_number_verified !== 2) {
            pan_card_number_verified = getValues('is_pan_card_number_verified');
        }
        if (parseInt(pan_card_number_verified) === 1 && status === 2) {
            // Swal.fire({
            //     title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            //     text: VALIDATION_MESSAGE?.REJECTED_VERIFIED,
            //     icon: 'warning',
            //     confirmButtonColor: '#31CE36',
            //     confirmButtonText: 'Ok'
            // });
            Swal.fire(VALIDATION_MESSAGE?.REJECTED_VERIFIED);
        }
        else if (parseInt(pan_card_number_verified) === 2 && status === 1) {
            // Swal.fire({
            //     title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            //     text: VALIDATION_MESSAGE?.APPROVED_UNVERFIED,
            //     icon: 'warning',
            //     confirmButtonColor: '#31CE36',
            //     confirmButtonText: 'Ok'
            // });
            Swal.fire(VALIDATION_MESSAGE?.APPROVED_UNVERFIED);
        }
        else {
            var text_message = '';
            if (status === 0) {
                text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_PENDING;
            }
            else if (status === 1) {
                text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_APPROVE;
            }
            else if (status === 2) {
                text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_DECLINE;
            }
            else if (status === 5) {
                text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_USER_DEACTIVATE;
            }
            Swal.fire({
                title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
                text: text_message,
                icon: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#31CE36',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Ok'
            }).then((result) => {
                if (result.isConfirmed) {
                    statusChange(id, status);
                }
            });
        }
    };

    const statusChange = async (id: number, status: number) => {
        var params: any = {
            "user_id": id,
            "status": status
        }
        if (data_admin_get_by_id_user?.data?.is_pan_card_number_verified !== 1 && data_admin_get_by_id_user?.data?.is_pan_card_number_verified !== 2) {
            // params['is_pan_card_number_verified'] = getValues('is_pan_card_number_verified');
            var get_is_pan_card_number_verified = getValues('is_pan_card_number_verified');
            params['is_pan_card_number_verified'] = Number(get_is_pan_card_number_verified);
        }
        dispatch(AdminChangeStatusUserAction(params));
    };
    useEffect(() => {
        var params = {
            user_id: decodeID(viewId)
        };
        dispatch(AdminGetByIdUserAction(params));        
    }, [dispatch, status_admin_get_by_id_user, data_admin_change_status_user]);

    return (
        <Fragment>
            {loading_admin_get_by_id_user ? <Spinner />:""}
            {loading_admin_change_status_user ? <Spinner />:""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Buyer Details</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "buyer"}>
                                            Buyer Management
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                            {status_admin_get_by_id_user ?
                                <div className="p-4 view-data-form card">
                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label>Full Name </label>
                                            <p>{data_admin_get_by_id_user?.data?.full_name}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label>Company Name</label>
                                            <p>{data_admin_get_by_id_user?.data?.company_name}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <div className="verified-field">
                                                <label>Email</label>
                                                {(() => {
                                                    if (data_admin_get_by_id_user?.data?.email_verify === 1) {
                                                        return (
                                                            <span className="ms-auto verify"><span className="icon-verified"></span> Verified</span>
                                                        )
                                                    } else {
                                                        return (
                                                            <span className="ms-auto unverify"><i className="icon-unferified"></i> Unverified</span>
                                                        )
                                                    }
                                                })()}
                                            </div>
                                            <p>{data_admin_get_by_id_user?.data?.email}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label>Mobile Number</label>
                                            <p>+{data_admin_get_by_id_user?.data?.country_code} - {data_admin_get_by_id_user?.data?.mobile_number}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <div className="verified-field">
                                                <label>Pan Card Number</label>
                                                {(() => {
                                                    if (data_admin_get_by_id_user?.data?.is_pan_card_number_verified === 1) {
                                                        return (
                                                            <span className="ms-auto verify"><span className="icon-verified"></span> Verified</span>
                                                        )
                                                    } else if (data_admin_get_by_id_user?.data?.is_pan_card_number_verified === 2) {
                                                        return (
                                                            <span className="ms-auto unverify"><i className="icon-unferified"></i> Unverified</span>
                                                        )
                                                    } else {
                                                        return (
                                                            <div className="form-group-checkbox-span-main">
                                                                <div className="checkbox-span-verified">
                                                                    <input
                                                                        type="radio"
                                                                        className="form-check-input"
                                                                        value={1}
                                                                        {...register('is_pan_card_number_verified')}
                                                                    /> Verified
                                                                </div>
                                                                <div className="checkbox-span-unverified">
                                                                    <input
                                                                        type="radio"
                                                                        className="form-check-input"
                                                                        value={2}
                                                                        {...register('is_pan_card_number_verified')}
                                                                        defaultChecked={true}
                                                                    /> Unverified
                                                                </div>
                                                            </div>
                                                        )
                                                    }
                                                })()}
                                            </div>
                                            <p>{data_admin_get_by_id_user?.data?.pan_card_number}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label>Registration date</label>
                                            <p>{getDate(data_admin_get_by_id_user?.data?.createdAt)}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label>Status</label>
                                            {(() => {
                                                var statusGet = statusOption().filter(option => option.value === data_admin_get_by_id_user?.data?.status);
                                                if (data_admin_get_by_id_user?.data?.status === 1) {
                                                    return (
                                                        <p>{statusGet[0]?.label}</p>
                                                    )
                                                } else if (data_admin_get_by_id_user?.data?.status === 2) {
                                                    return (
                                                        <p>{statusGet[0]?.label}</p>
                                                    )
                                                } else {
                                                    return (
                                                        <Controller
                                                            control={control}
                                                            name={"status"}
                                                            render={(props) => (
                                                                <Select
                                                                    placeholder="Approval Pending"
                                                                    // value={
                                                                    //     viewStatusOption().filter(option => option.value === data_admin_get_by_id_user?.data?.status)
                                                                    // }
                                                                    onChange={(e: any) => {
                                                                        confirmStatusChange(data_admin_get_by_id_user?.data?.id, e?.value);
                                                                    }}
                                                                    options={viewStatusOption()}
                                                                    classNamePrefix="dropDown"
                                                                    isMulti={false}
                                                                    className="react-select"
                                                                />
                                                            )}
                                                        />
                                                    )
                                                }
                                            })()}
                                        </div>
                                    </div>
                                </div>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default ViewBuyer;
