import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Swal from 'sweetalert2'

import { AdminGetNewsletterAction, AdminDeleteNewsletterAction } from '../../Redux/Actions/NewsletterActions';
import { REACT_APP_BASE_URL, REACT_PAGINATE_LIMIT, REACT_PAGINATE_NEXT_LABEL, REACT_PAGINATE_PREVIOUS_LABEL, REACT_PAGINATE_BREAK_LABEL } from '../../Config/Config';
import ReactPaginate from 'react-paginate';
import { toast } from 'react-toastify';

import { VALIDATION_MESSAGE } from '../../Constants/Constants';

import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';
const Category = () => {
    const dispatch = useDispatch<any>();
    const [paginateLimit, setPaginateLimit] = useState(REACT_PAGINATE_LIMIT);
    const [paginatePage, setPaginatePage] = useState(1);
    const [search, setSearch] = useState('');
    const [paginateCount, setPaginateCount] = useState(0);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_get_newsletter, data_admin_get_newsletter, error_admin_get_newsletter, loading_admin_get_newsletter } = useSelector((state: any) => state.AdminGetNewsletterState);
    const { status_admin_delete_newsletter, data_admin_delete_newsletter, error_admin_delete_newsletter, loading_admin_delete_newsletter } = useSelector((state: any) => state.AdminDeleteNewsletterState);


    const confirmDelete = async (delete_id: number) => {
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: VALIDATION_MESSAGE?.ARE_YOU_DELETE_NEWSLETTER,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                deleteData(delete_id);
            }
        });
    };

    const deleteData = async (delete_id: number) => {
        const params = {
            "delete_id": delete_id
        }
        dispatch(AdminDeleteNewsletterAction(params));
    };

    const handlePaginateClick = (event: any) => {
        var next_page_set = event.selected + 1;
        setPaginatePage(next_page_set);
        const params = {
            "search": search,
            "page": next_page_set,
            "limit": paginateLimit
        }
        dispatch(AdminGetNewsletterAction(params));
    };

    const handleSearch = (event: any) => {
        if (event.keyCode === 13) {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetNewsletterAction(params));
        }
        else if (event.target.value === '') {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetNewsletterAction(params));
        }
    }

    useEffect(() => {
        const params = {
            "search": search,
            "page": paginatePage,
            "limit": paginateLimit
        }
        dispatch(AdminGetNewsletterAction(params));
    }, []);

    useEffect(() => {
        const params = {
            "search": search,
            "page": paginatePage,
            "limit": paginateLimit
        }
        if (status_admin_delete_newsletter) {
            dispatch(AdminGetNewsletterAction(params));
            toast.success(data_admin_delete_newsletter?.message);
            dispatch(AdminDeleteNewsletterAction('RESET'));
        }
        setPaginateCount(Math.ceil(data_admin_get_newsletter?.data?.count / paginateLimit));
    }, [dispatch, status_admin_get_newsletter, status_admin_delete_newsletter]);
    console.log(data_admin_get_newsletter,'data_admin_get_newsletterdata_admin_get_newsletter')
    return (
        <Fragment>
            {loading_admin_get_newsletter || loading_admin_delete_newsletter ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Newsletter Management</h4>
                            </div>
                            <div className="row align-items-center mb-3">
                                <div className="col">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="row">
                                            <div className="col-lg-4 col-md-4 pl-0">
                                                <div className="input-group">
                                                    <input type="text" className="form-control" onKeyDown={handleSearch} onChange={handleSearch} placeholder="Search ..." />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="card listing_c order_managment card-list">
                                <div className="row align-items-center">
                                    <div className="col-lg-1 col-md-1">
                                        <h6 className="fw-bold mb-1">Serial No</h6>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <h6 className="fw-bold mb-1">Name of the user</h6>
                                    </div>
                                    <div className="col-lg-4 col-md-4">
                                        <h6 className="fw-bold mb-1">Email ID of the user</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2 text-center">
                                        <h6 className="fw-bold mb-1"> Remove</h6>
                                    </div>
                                </div>
                                {data_admin_get_newsletter?.data?.rows?.length > 0 ?
                                    data_admin_get_newsletter?.data?.rows?.map((d: any, i: any) => {
                                        return (
                                            <div className="row align-items-center" key={i}>
                                                <div className="col-lg-1 col-md-1 text-center">
                                                    <div className="pro_name">
                                                        {(paginatePage - 1) * paginateLimit + i + 1}
                                                    </div>
                                                </div>
                                                <div className="col-lg-4 col-md-4">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.name}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-4 col-md-4">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.name}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="list_right text-center">
                                                        <span className="span-fa-trash" onClick={() => { confirmDelete(d?.id); }}>
                                                            <i className="fas fa-trash-alt" title="Delete"></i>
                                                        </span>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                                    : "No data found."}
                            </div>
                            {paginateCount > 0 ?
                                <div className="col-sm-12 react-paginate cutome-react-paginate justify-content-center">
                                    <ReactPaginate
                                        className="pagination justify-content-center"
                                        breakLabel={REACT_PAGINATE_BREAK_LABEL}
                                        nextLabel={REACT_PAGINATE_NEXT_LABEL}
                                        previousLabel={REACT_PAGINATE_PREVIOUS_LABEL}
                                        pageRangeDisplayed={paginateLimit}
                                        onPageChange={handlePaginateClick}
                                        pageCount={paginateCount}
                                        breakClassName={'page-item'}
                                        breakLinkClassName={'page-link'}
                                        containerClassName={'pagination'}
                                        pageClassName={'page-item'}
                                        pageLinkClassName={'page-link'}
                                        previousClassName={'page-item'}
                                        previousLinkClassName={'page-link'}
                                        nextClassName={'page-item'}
                                        nextLinkClassName={'page-link'}
                                        activeClassName={'active'}
                                    />
                                </div>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment >
    );
}

export default Category;
