import React,{Fragment, useState, useEffect} from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import { AdminGetParentCategoryAction, AdminEditCategoryAction, AdminGetByIdCategoryAction } from '../../Redux/Actions/CategoryActions';
import Header from '../../Layouts/Header';
import Footer from '../../Layouts/Footer';
import Sidebar from '../../Layouts/Sidebar';
import Spinner from '../../Layouts/Spinner';
import { Controller, useForm } from 'react-hook-form';
import { decodeID, getLastURL } from '../../Utils/Helper';


const EditCategoryForm = () => {
    const dispatch = useDispatch<any>();
    const location = useLocation()
    const editId = getLastURL(location)

    const[getParentCategory, setGetParentCategory] = useState<any[]>([]);

  const{status_admin_get_parent_category, data_admin_get_parent_category, error_admin_get_parent_category, loading_admin_get_parent_category } = useSelector((state: any) => state.AdminGetParentCategoryState);
  const{status_admin_get_by_id_category, data_admin_get_by_id_category, error_admin_get_by_id_category, loading_admin_get_by_id_category } = useSelector((state: any) => state.AdminGetParentCategoryState);
  const { status_admin_edit_category, data_admin_edit_category, error_admin_edit_category, loading_admin_edit_category } = useSelector((state: any) => state.AdminEditCategoryState);

  const{ status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState)
  const{ status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState)

  const {
    handleSubmit,
    register,
    setValue,
    getValues,
    formState: {errors},
    control
  } = useForm({
        mode: 'onChange',
        defaultValues: {
            id: decodeID(editId),
            name: "",
            parentId: 0,
            getParentId: 0,
            category_image: "",
            category_image_full: "",
        }
  })

  const onSubmit = async(payload: any) => {
    dispatch(AdminEditCategoryAction(payload))
  }

  const set_Value = async(data: any) => {
    setValue("name", data?.name);
    setValue("parentId", data?.parentId);
    setValue("getParentId", data?.parentId);
    setValue("category_image_full", data?.category_image_full);
  }


  useEffect(() => {
    dispatch(AdminGetParentCategoryAction({}));
    var params = {
        id: decodeID(editId)
    }
    if(status_admin_get_by_id_category){
        set_Value(data_admin_get_by_id_category?.data);
    }
    else{
        dispatch(AdminGetParentCategoryAction(params))
    }
  },[status_admin_get_by_id_category]);

  return (
    <>
    {loading_admin_get_by_id_category || loading_admin_edit_category ? <Spinner/> : "" }
       <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
          <Header/>
          <Sidebar/>
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Edit Category</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                     <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                      </Link>

                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                       <Link to={REACT_APP_BASE_URL + "category"}>
                                            Category Management
                                       </Link>
                                    </li>
                                </ul>
                            </div>
                            {status_admin_get_by_id_category ?
                                <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                    <div className="p-4">
                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                               {(() => {
                                                    if(getValues('getParentId') && getValues('getParentId')>0){
                                                        return(
                                                            <Fragment>
                                                                <label className="form-label">Name of category *</label>
                                                                <input
                                                                    type="text"
                                                                    placeholder="Enter Name Of Category"
                                                                    className="form-control"
                                                                    minLength={1}
                                                                    maxLength={30}
                                                                    {...register('name', {
                                                                        onChange: (e) => setValue(`name`, e?.target?.value)
                                                                    })}
                                                                />
                                                            </Fragment>
                                                         )}
                                                         else{
                                                            return(
                                                                <Fragment>
                                                                <label className="form-label">Name of category</label>
                                                                <input
                                                                    type="text"
                                                                    placeholder="Enter Name Of Category"
                                                                    className="form-control"
                                                                    minLength={1}
                                                                    maxLength={30}
                                                                    {...register('name', { disabled: true })}
                                                                />
                                                            </Fragment>
                                                            )
                                                         }

                                                       })()}
                                              <p className="text-danger">{errors?.name?.message ?? error_admin_edit_category?.errors?.name?.msg}</p>
                                            </div>
                                            {(() => {
                                                 if(getValues('getParentId') && getValues('getParentId')>0){
                                                   return(
                                                    <Fragment>
                                                            <div className="form-group col-md-6">
                                                                <label className="form-label">Name of parent category</label>
                                                                <Controller
                                                                    control={control}
                                                                    name={"parentId"}
                                                                    render={(props) => (
                                                                        <Select
                                                                            placeholder="Select Parent Category"
                                                                            value={
                                                                                getParentCategory.filter(option => option.value === getValues('parentId'))
                                                                            }
                                                                            onChange={(e: any) => {
                                                                                setValue(`parentId`, e?.value);
                                                                            }}
                                                                            options={getParentCategory}
                                                                            classNamePrefix="dropDown"
                                                                            isMulti={false}
                                                                            className="react-select"
                                                                        />
                                                                    )}
                                                                />
                                                                <p className="text-danger">{errors?.parentId?.message ?? error_admin_edit_category?.errors?.parentId?.msg}</p>
                                                            </div>
                                                        </Fragment>
                                                   )
                                                 }
                                                 else{
                                                    return(
                                                        <Fragment></Fragment>
                                                    )
                                                 }

                                            })()}

                                         <div className="form-group col-md-6 form-group-checkbox">
                                                <label className="form-label">Image of category</label>

                                                <input
                                                    type="file"
                                                    accept="image/jpeg,image/png,image/jpg"
                                                    className="form-control"

                                                />
                                                <p className="text-danger"></p>
                                            </div>
                                        </div>
                                        <div className="mt-3 text-center">
                                            <button type="submit" className="btn btn-primary">Save</button>
                                        </div>
                                    </div>
                                </form>
                                   : '' }
                        </div>
                    </div>
                   <Footer/>
                </div>
            </div>
    </>
  )
}

export default EditCategoryForm;
