import Swal from 'sweetalert2'

import { AdminGetBannerAction, AdminChangeStatusBannerAction, AdminDeleteBannerAction, AdminGetByIdBannerAction } from '../../Redux/Actions/BannerActions';
import { REACT_APP_BASE_URL, REACT_PAGINATE_LIMIT, REACT_PAGINATE_NEXT_LABEL, REACT_PAGINATE_PREVIOUS_LABEL, REACT_PAGINATE_BREAK_LABEL } from '../../Config/Config';
import ReactPaginate from 'react-paginate';
import { toast } from 'react-toastify';
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";

import { bannerStatusOption, bannerTypeFillterOption, encodeID } from "../../Utils/Helper";
import { VALIDATION_MESSAGE } from '../../Constants/Constants';

import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';
const Banner = () => {
    const dispatch = useDispatch<any>();
    const navigate = useNavigate();
    const [paginateLimit, setPaginateLimit] = useState(REACT_PAGINATE_LIMIT);
    const [paginatePage, setPaginatePage] = useState(1);
    const [search, setSearch] = useState('');
    const [bannerStatus, setBannerStatus] = useState(404);
    const [bannerType, setBannerType] = useState(404);
    const [paginateCount, setPaginateCount] = useState(0);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_get_banner, data_admin_get_banner, error_admin_get_banner, loading_admin_get_banner } = useSelector((state: any) => state.AdminGetBannerState);
    const { status_admin_change_status_banner, data_admin_change_status_banner, error_admin_change_status_banner, loading_admin_change_status_banner } = useSelector((state: any) => state.AdminChangeStatusBannerState);
    const { status_admin_delete_banner, data_admin_delete_banner, error_admin_delete_banner, loading_admin_delete_banner } = useSelector((state: any) => state.AdminDeleteBannerState);

    const {
        control,
    } = useForm({});

    const confirmStatusChange = async (id: number, status: number) => {
        var text_message = '';
        if (status === 0) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_BANNER_DISABLE;
        }
        else if (status === 1) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_BANNER_ENABLE;
        }
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: text_message,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                statusChange(id, status);
            }
        });
    };

    const statusChange = async (id: number, status: number) => {
        const params = {
            "id": id,
            "status": status
        }
        dispatch(AdminChangeStatusBannerAction(params));
    };

    const confirmDelete = async (delete_id: number) => {
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: VALIDATION_MESSAGE?.ARE_YOU_DELETE_BANNER,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                deleteData(delete_id);
            }
        });
    };

    const deleteData = async (delete_id: number) => {
        const params = {
            "delete_id": delete_id
        }
        dispatch(AdminDeleteBannerAction(params));
    };

    const editData = async (user_id: number) => {
        dispatch(AdminGetByIdBannerAction('RESET'));
        navigate(REACT_APP_BASE_URL + 'banner-edit/' + encodeID(user_id), { replace: true })
    };

    const handlePaginateClick = (event: any) => {
        var next_page_set = event.selected + 1;
        setPaginatePage(next_page_set);
        const params = {
            "search": search,
            "type": bannerType,
            "status": bannerStatus,
            "page": next_page_set,
            "limit": paginateLimit
        }
        dispatch(AdminGetBannerAction(params));
    };

    const handleSearch = (event: any) => {
        if (event.keyCode === 13) {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "type": bannerType,
                "status": bannerStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetBannerAction(params));
        }
        else if (event.target.value === '') {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "search": event.target.value,
                "type": bannerType,
                "status": bannerStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetBannerAction(params));
        }
    }

    const bannerTypeFilltr = (type: any) => {
        setPaginatePage(1);
        setBannerType(type);
        const params = {
            "search": search,
            "type": type,
            "status": bannerStatus,
            "page": 1,
            "limit": paginateLimit
        }
        dispatch(AdminGetBannerAction(params));
    }

    const bannerStatusFilltr = (status: any) => {
        setPaginatePage(1);
        setBannerStatus(status);
        const params = {
            "search": search,
            "type": bannerType,
            "status": status,
            "page": 1,
            "limit": paginateLimit
        }
        dispatch(AdminGetBannerAction(params));
    }

    useEffect(() => {
        const params = {
            "search": search,
            "type": bannerType,
            "status": bannerStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        dispatch(AdminGetBannerAction(params));
    }, []);

    useEffect(() => {
        const params = {
            "search": search,
            "type": bannerType,
            "status": bannerStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        if (status_admin_change_status_banner) {
            dispatch(AdminGetBannerAction(params));
            toast.success(data_admin_change_status_banner?.message);
            dispatch(AdminChangeStatusBannerAction('RESET'));
        }
        if (status_admin_delete_banner) {
            dispatch(AdminGetBannerAction(params));
            toast.success(data_admin_delete_banner?.message);
            dispatch(AdminDeleteBannerAction('RESET'));
        }
        setPaginateCount(Math.ceil(data_admin_get_banner?.data?.count / paginateLimit));
    }, [dispatch, data_admin_get_banner, status_admin_change_status_banner, status_admin_delete_banner]);
    return (
        <Fragment>
            {loading_admin_get_banner || loading_admin_change_status_banner || loading_admin_delete_banner ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Banner Management</h4>
                            </div>
                            <div className="row align-items-center mb-3">
                                <div className="col">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="row">
                                            <div className="col-lg-4 col-md-4 pl-0">
                                                <div className="input-group">
                                                    <input type="text" className="form-control" onKeyDown={handleSearch} onChange={handleSearch} placeholder="Search ..." />
                                                </div>
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Select Banner Type"
                                                            onChange={(e: any) => {
                                                                bannerTypeFilltr(e?.value);
                                                            }}
                                                            options={bannerTypeFillterOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Banner Status"
                                                            onChange={(e: any) => {
                                                                bannerStatusFilltr(e?.value);
                                                            }}
                                                            options={bannerStatusOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-auto">
                                    <Link to={REACT_APP_BASE_URL + "banner-create"} className="btn btn-primary ml-2">
                                        Create Banner
                                    </Link>
                                </div>
                            </div>
                            <div className="card listing_c order_managment card-list">
                                <div className="row align-items-center">
                                    <div className="col-lg-1 col-md-1">
                                        <h6 className="fw-bold mb-1">Serial No</h6>
                                    </div>
                                    <div className="col-lg-7 col-md-7">
                                        <h6 className="fw-bold mb-1">Name of the banner</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Status</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Action</h6>
                                    </div>
                                </div>
                                {data_admin_get_banner?.data?.rows?.length > 0 ?
                                    data_admin_get_banner?.data?.rows.map((d: any, i: any) => {
                                        var get_status = bannerStatusOption().filter(option => option.value === d?.status);
                                        return (
                                            <div className="row align-items-center" key={i}>
                                                <div className="col-lg-1 col-md-1  text-center">
                                                    <div className="pro_name">
                                                        {/* <img src={String(ImageHelper.AvatarImage)} alt="..." className="avatar-img rounded-circle" /> */}
                                                        {(paginatePage - 1) * paginateLimit + i + 1}
                                                    </div>
                                                </div>
                                                <div className="col-lg-7 col-md-7">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.title}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="pro_name">
                                                        {get_status[0]?.label}
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="list_right">
                                                        <span className="span-fa-edit" onClick={() => { editData(d?.id); }}>
                                                            <i className="fas fa-edit" title="Edit"></i>
                                                        </span>
                                                        <span className="span-fa-trash" onClick={() => { confirmDelete(d?.id); }}>
                                                            <i className="fas fa-trash-alt" title="Delete"></i>
                                                        </span>
                                                        {(() => {
                                                            if (d?.status === 1) {
                                                                return (
                                                                    <span className="span-fa-disable" onClick={() => { confirmStatusChange(d?.id, 0); }}>
                                                                        <i className="fas fa-ban" title="Disable"></i>
                                                                    </span>
                                                                )
                                                            } else if (d?.status === 0) {
                                                                return (
                                                                    <span className="span-fa-enable" onClick={() => { confirmStatusChange(d?.id, 1); }}>
                                                                        <i className="fas fa-check" title="Enable"></i>
                                                                    </span>
                                                                )
                                                            }
                                                        })()}
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                                    : "No data found."}
                            </div>
                            {paginateCount > 0 ?
                                <div className="col-sm-12 react-paginate cutome-react-paginate justify-content-center">
                                    <ReactPaginate
                                        className="pagination justify-content-center"
                                        breakLabel={REACT_PAGINATE_BREAK_LABEL}
                                        nextLabel={REACT_PAGINATE_NEXT_LABEL}
                                        previousLabel={REACT_PAGINATE_PREVIOUS_LABEL}
                                        pageRangeDisplayed={paginateLimit}
                                        onPageChange={handlePaginateClick}
                                        pageCount={paginateCount}
                                        breakClassName={'page-item'}
                                        breakLinkClassName={'page-link'}
                                        containerClassName={'pagination'}
                                        pageClassName={'page-item'}
                                        pageLinkClassName={'page-link'}
                                        previousClassName={'page-item'}
                                        previousLinkClassName={'page-link'}
                                        nextClassName={'page-item'}
                                        nextLinkClassName={'page-link'}
                                        activeClassName={'active'}
                                    />
                                </div>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment >
    );
}

export default Banner;
