import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { AdminAddProductAction } from '../../Redux/Actions/ProductActions';
import { AdminGetParentCategoryAction, AdminGetChildCategoryAction } from '../../Redux/Actions/CategoryActions';
import { AdminGetActiveManufacturerAction } from '../../Redux/Actions/ManufacturerActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { toast } from 'react-toastify';
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';

const AddProductForm = () => {
    const dispatch = useDispatch<any>();
    const [getParentCategory, setParentCategory] = useState<any[]>([]);
    const [getChildCategory, setChildCategory] = useState<any[]>([]);
    const [getManufacturer, setManufacturer] = useState<any[]>([]);
    // const [productImages, setProductImages] = useState<any[]>([]);
    const [productImages, setProductImages] = useState('');

    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_get_parent_category, data_admin_get_parent_category, error_admin_get_parent_category, loading_admin_get_parent_category } = useSelector((state: any) => state.AdminGetParentCategoryState);
    const { status_admin_get_child_category, data_admin_get_child_category, error_admin_get_child_category, loading_admin_get_child_category } = useSelector((state: any) => state.AdminGetChildCategoryState);
    const { status_admin_get_active_manufacturer, data_admin_get_active_manufacturer, error_admin_get_active_manufacturer, loading_admin_get_active_manufacturer } = useSelector((state: any) => state.AdminGetActiveManufacturerState);
    const { status_admin_add_product, data_admin_add_product, error_admin_add_product, loading_admin_add_product } = useSelector((state: any) => state.AdminAddProductState);

    const formSchema = Yup.object().shape({
        name: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.PRODUCT_NAME_REQUIRED)
            .max(50, VALIDATION_MESSAGE?.PRODUCT_NAME_MAX),

        // pp_the_price_of_product: Yup.string()
        //     .required(VALIDATION_MESSAGE?.PRODUCT_PRICE_REQUIRED)
        //     .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
        //     .matches(new RegExp('[0-9]{1}'), VALIDATION_MESSAGE?.PRODUCT_PRICE_VALID)
        //     .max(50, VALIDATION_MESSAGE?.PRODUCT_PRICE_MAX),

        product_image: Yup.mixed()
            .test("required", VALIDATION_MESSAGE?.PRODUCT_IMAGE_REQUIRED, (file) => {
                if (file) return true;
                return false;
            })
            .test("fileType", VALIDATION_MESSAGE?.PRODUCT_UNSUPPORTED, (file) => {
                if (file.length > 0) {
                    var file_type = file.length && ["image/jpeg", "image/png", "image/jpg"].includes(file[0].type)
                    if (file_type) {
                        return file_type;
                    }
                    else {
                        setValue('product_image', '');
                        return file_type;
                    }
                }
                else {
                    return true;
                }
            }),

        parent_category: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_CATEGORY_REQUIRED),

        manufacturer: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_MANUFACTURER_REQUIRED),

        description: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_DESCRIPTION_REQUIRED)
            .trim(VALIDATION_MESSAGE?.PRODUCT_DESCRIPTION_REQUIRED)
            .max(250, VALIDATION_MESSAGE?.PRODUCT_DESCRIPTION_MAX),

        specification_and_features: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_SPECIFICATION_FEATURES_REQUIRED)
            .trim(VALIDATION_MESSAGE?.PRODUCT_SPECIFICATION_FEATURES_REQUIRED)
            .max(500, VALIDATION_MESSAGE?.PRODUCT_SPECIFICATION_FEATURES_MAX),

        quantity_available: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_QUANTITY_AVAILABLE_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(new RegExp('[0-9]{1}'), VALIDATION_MESSAGE?.PRODUCT_QUANTITY_AVAILABLE_VALID)
            .max(50, VALIDATION_MESSAGE?.PRODUCT_QUANTITY_AVAILABLE_MAX),

        sku_code: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_SKU_CODE_REQUIRED),

        hsn_code: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_HSN_CODE_REQUIRED),

        sac_code: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_SAC_CODE_REQUIRED),

        tax: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_TAX_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(new RegExp('[0-9]{1}'), VALIDATION_MESSAGE?.PRODUCT_TAX_VALID)
            .max(5, VALIDATION_MESSAGE?.PRODUCT_TAX_MAX),

        mark_featured: Yup.string()
            .required(VALIDATION_MESSAGE?.PRODUCT_FEATURED_PRODUCT_REQUIRED),

    });

    const {
        register,
        handleSubmit,
        reset,
        setValue,
        getValues,
        formState: { errors },
        control,
    } = useForm({
        resolver: yupResolver(formSchema),
        mode: 'onChange',
        defaultValues: {
            name: "",
            // pp_the_price_of_product: "",
            product_image: "",
            parent_category: "",
            child_category: "",
            manufacturer: "",
            description: "",
            specification_and_features: "",
            quantity_available: "",
            sku_code: "",
            hsn_code: "",
            sac_code: "",
            tax: "",
            mark_featured: 0,
        }
    });

    const selectFileHandler = (e: any) => {
        // const files = e.target.files;
        // if (files !== '') {
        //     setProductImages(files);
        // }
        let file = e.target.files[0];
        if (file !== '') {
            setProductImages(file);
        }
    }

    const onSubmit = async (payload: any) => {
        delete payload['product_image'];
        if (productImages != '') {
            payload['product_image'] = productImages;
        }
        dispatch(AdminAddProductAction(payload,productImages));
    };

    useEffect(() => {
        dispatch(AdminGetParentCategoryAction({}));
        dispatch(AdminGetActiveManufacturerAction({}));
    }, []);
    useEffect(() => {
        if (status_admin_get_parent_category) {
            if (data_admin_get_parent_category && data_admin_get_parent_category?.status === true && data_admin_get_parent_category?.data?.length > 0) {
                var get_parent_category: any[] = [];
                get_parent_category.push({
                    label: "Select Parent Category",
                    value: 0,
                });
                data_admin_get_parent_category?.data?.map((d: any, i: any) => {
                    get_parent_category.push({
                        label: d?.name,
                        value: d?.id,
                    });
                });
                setParentCategory(get_parent_category);
            }
        }
        if (status_admin_get_active_manufacturer) {
            if (data_admin_get_active_manufacturer && data_admin_get_active_manufacturer?.status === true && data_admin_get_active_manufacturer?.data?.length > 0) {
                var get_manufacturer: any[] = [];
                get_manufacturer.push({
                    label: "Select Manufacturer",
                    value: 0,
                });
                data_admin_get_active_manufacturer?.data?.map((d: any, i: any) => {
                    get_manufacturer.push({
                        label: d?.name,
                        value: d?.id,
                    });
                });
                setManufacturer(get_manufacturer);
            }
        }
        if (status_admin_get_child_category) {
            var get_child_category: any[] = [];
            if (data_admin_get_child_category && data_admin_get_child_category?.status === true && data_admin_get_child_category?.data?.length > 0) {
                get_child_category.push({
                    label: "Select Child Category",
                    value: 0,
                });
                data_admin_get_child_category?.data?.map((d: any, i: any) => {
                    get_child_category.push({
                        label: d?.name,
                        value: d?.id,
                    });
                });
                setChildCategory(get_child_category);
            }
        }
        if (status_admin_add_product) {
            toast.success(data_admin_add_product?.message);
            dispatch(AdminAddProductAction('RESET',null));
            reset();
        }
    }, [dispatch, status_admin_get_parent_category, status_admin_get_active_manufacturer, status_admin_get_child_category, status_admin_add_product]);
    return (
        <Fragment>
            {loading_admin_add_product ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Add Product</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "product"}>
                                            Product Management
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                            <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                <div className="p-4">
                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Name of product *</label>
                                            <input
                                                type="text"
                                                placeholder="Enter Name Of Product"
                                                className="form-control"
                                                minLength={1}
                                                maxLength={50}
                                                {...register('name')}
                                            />
                                            <p className="text-danger">{errors?.name?.message ?? error_admin_add_product?.errors?.name?.msg}</p>
                                        </div>
                                        {/* <div className="form-group col-md-6">
                                            <label className="form-label">The price of product</label>
                                            <input
                                                type="number"
                                                placeholder="Enter The Price Of Product"
                                                className="form-control"
                                                {...register('pp_the_price_of_product', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 50) {
                                                            setValue(`pp_the_price_of_product`, e.target.value.slice(0, 50))
                                                        }
                                                        else {
                                                            setValue(`pp_the_price_of_product`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.pp_the_price_of_product?.message ?? error_admin_add_product?.errors?.pp_the_price_of_product?.msg}</p>
                                        </div> */}
                                        <div className="form-group col-md-6 form-group-checkbox">
                                            <label className="form-label">Image of the product (10 Max)</label>
                                            <input
                                                type="file"
                                                accept="image/jpeg,image/png,image/jpg"
                                                className="form-control"
                                                {...register('product_image', {
                                                    onChange: (e) => { selectFileHandler(e) }
                                                })}
                                                // multiple={true}
                                            />
                                            <p className="text-danger">{errors?.product_image?.message ?? error_admin_add_product?.errors?.product_image?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Parent Category</label>
                                            <Controller
                                                control={control}
                                                name={"parent_category"}
                                                render={(props) => (
                                                    <Select
                                                        placeholder="Select Parent Category"
                                                        // value={
                                                        //     getParentCategory.filter(option => option.value === getValues('parent_category'))
                                                        // }
                                                        onChange={(e: any) => {
                                                            setValue(`parent_category`, e?.value);
                                                            setChildCategory([]);
                                                            dispatch(AdminGetChildCategoryAction({ "parentId": e?.value }));
                                                        }}
                                                        options={getParentCategory}
                                                        classNamePrefix="dropDown"
                                                        isMulti={false}
                                                        className="react-select"
                                                    />
                                                )}
                                            />
                                            <p className="text-danger">{errors?.parent_category?.message ?? error_admin_add_product?.errors?.parent_category?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Child category</label>
                                            <Controller
                                                control={control}
                                                name={"child_category"}
                                                render={(props) => (
                                                    <Select
                                                        placeholder="Select Child Category"
                                                        // value={
                                                        //     getChildCategory.filter(option => option.value === getValues('child_category'))
                                                        // }
                                                        onChange={(e: any) => {
                                                            setValue(`child_category`, e?.value);
                                                        }}
                                                        options={getChildCategory}
                                                        classNamePrefix="dropDown"
                                                        isMulti={false}
                                                        className="react-select"
                                                    />
                                                )}
                                            />
                                            <p className="text-danger">{errors?.child_category?.message ?? error_admin_add_product?.errors?.child_category?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Manufacturer</label>
                                            <Controller
                                                control={control}
                                                name={"manufacturer"}
                                                render={(props) => (
                                                    <Select
                                                        placeholder="Select Manufacturer"
                                                        // value={
                                                        //     getChildCategory.filter(option => option.value === getValues('manufacturer'))
                                                        // }
                                                        onChange={(e: any) => {
                                                            setValue(`manufacturer`, e?.value);
                                                        }}
                                                        options={getManufacturer}
                                                        classNamePrefix="dropDown"
                                                        isMulti={false}
                                                        className="react-select"
                                                    />
                                                )}
                                            />
                                            <p className="text-danger">{errors?.manufacturer?.message ?? error_admin_add_product?.errors?.manufacturer?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Quantity available</label>
                                            <input
                                                type="number"
                                                placeholder="Enter Quantity Available"
                                                className="form-control"
                                                {...register('quantity_available', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 50) {
                                                            setValue(`quantity_available`, e.target.value.slice(0, 50))
                                                        }
                                                        else {
                                                            setValue(`quantity_available`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.quantity_available?.message ?? error_admin_add_product?.errors?.quantity_available?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Description of product</label>
                                            <textarea
                                                placeholder="Enter Description Of Product"
                                                className="form-control"
                                                rows={4}
                                                cols={50}
                                                minLength={1}
                                                maxLength={250}
                                                {...register('description')}
                                            >
                                            </textarea>
                                            <p className="text-danger">{errors?.description?.message ?? error_admin_add_product?.errors?.description?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Specification & features</label>
                                            <textarea
                                                placeholder="Enter Specification & Features"
                                                className="form-control"
                                                rows={4}
                                                cols={50}
                                                minLength={1}
                                                maxLength={500}
                                                {...register('specification_and_features')}
                                            >
                                            </textarea>
                                            <p className="text-danger">{errors?.specification_and_features?.message ?? error_admin_add_product?.errors?.specification_and_features?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">SKU Code</label>
                                            <input
                                                type="text"
                                                placeholder="Enter SKU Code"
                                                className="form-control"
                                                {...register('sku_code')}
                                            />
                                            <p className="text-danger">{errors?.sku_code?.message ?? error_admin_add_product?.errors?.sku_code?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">HSN Code</label>
                                            <input
                                                type="text"
                                                placeholder="Enter HSN Code"
                                                className="form-control"
                                                {...register('hsn_code')}
                                            />
                                            <p className="text-danger">{errors?.hsn_code?.message ?? error_admin_add_product?.errors?.hsn_code?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">SAC Code</label>
                                            <input
                                                type="text"
                                                placeholder="Enter SAC Code"
                                                className="form-control"
                                                {...register('sac_code')}
                                            />
                                            <p className="text-danger">{errors?.sac_code?.message ?? error_admin_add_product?.errors?.sac_code?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Tax</label>
                                            <input
                                                type="number"
                                                placeholder="Enter Tax"
                                                className="form-control"
                                                {...register('tax', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 5) {
                                                            setValue(`tax`, e.target.value.slice(0, 5))
                                                        }
                                                        else {
                                                            setValue(`tax`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.tax?.message ?? error_admin_add_product?.errors?.tax?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Mark as a featured product</label>
                                            <div className="form-check pt-0 pb-0">
                                                <input
                                                    type="radio"
                                                    className="form-check-input ml-0"
                                                    id="featured_product_yes"
                                                    value={1}
                                                    {...register('mark_featured')}
                                                />
                                                <label className="form-check-label mb-0" htmlFor="featured_product_yes">&nbsp; Yes</label>
                                            </div>
                                            <div className="form-check pt-0 pb-0">
                                                <input
                                                    type="radio"
                                                    className="form-check-input ml-0"
                                                    id="featured_product_no"
                                                    value={0}
                                                    {...register('mark_featured')}
                                                    defaultChecked={true}
                                                />
                                                <label className="form-check-label mb-0" htmlFor="featured_product_no">&nbsp; No</label>
                                            </div>
                                            <p className="text-danger">{errors?.mark_featured?.message ?? error_admin_add_product?.errors?.mark_featured?.msg}</p>
                                        </div>
                                    </div>
                                    <div className="mt-3 text-center">
                                        <button type="submit" className="btn btn-primary">Save</button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default AddProductForm;
