import { Dispatch } from "react";
import {
    ADMIN_ADD_MANUFACTURER_RESET,
    ADMIN_ADD_MANUFACTURER_REQUEST,
    ADMIN_ADD_MANUFACTURER_SUCCESS,
    ADMIN_ADD_MANUFACTURER_FAIL,

    ADMIN_GET_MANUFACTURER_RESET,
    ADMIN_GET_MANUFACTURER_REQUEST,
    ADMIN_GET_MANUFACTURER_SUCCESS,
    ADMIN_GET_MANUFACTURER_FAIL,

    ADMIN_GET_ACTIVE_MANUFACTURER_RESET,
    ADMIN_GET_ACTIVE_MANUFACTURER_REQUEST,
