    ADMIN_MANUFACTURER_STATUS_CHANGE_SUCCESS,
    ADMIN_MANUFACTURER_STATUS_CHANGE_FAIL,

    ADMIN_MANUFACTURER_DELETE_RESET,
    ADMIN_MANUFACTURER_DELETE_REQUEST,
    ADMIN_MANUFACTURER_DELETE_SUCCESS,
    ADMIN_MANUFACTURER_DELETE_FAIL,

} from "../../Constants/ManufacturerConstants";
import { POSTAUTHAPI } from '../../Api/Index';

export const AdminAddManufacturerAction = (payload: any) => async (dispatch: Dispatch<any>) => {
    if (payload === 'RESET') {
        dispatch({ type: ADMIN_ADD_MANUFACTURER_RESET });
    }
    else {
        try {
            dispatch({
                type: ADMIN_ADD_MANUFACTURER_REQUEST
            });
            let data = await POSTAUTHAPI('add-banner', payload);
            if (data && data?.status === true) {
                dispatch({
                    type: ADMIN_ADD_MANUFACTURER_SUCCESS,
                    payload: data
                });
            }
            else {
                dispatch({
                    type: ADMIN_ADD_MANUFACTURER_FAIL,
                    payload: data
                });
            }
        }
        catch (error: any) {
            dispatch({
                type: ADMIN_ADD_MANUFACTURER_FAIL,
                payload: error.message
            });
        }
    }
};

