import {
    ADMIN_GET_NEWSLETTER_RESET,
    ADMIN_GET_NEWSLETTER_REQUEST,
    ADMIN_GET_NEWSLETTER_SUCCESS,
    ADMIN_GET_NEWSLETTER_FAIL,

    ADMIN_NEWSLETTER_DELETE_RESET,
    ADMIN_NEWSLETTER_DELETE_REQUEST,
    ADMIN_NEWSLETTER_DELETE_SUCCESS,
    ADMIN_NEWSLETTER_DELETE_FAIL,

} from "../../Constants/NewsletterConstants";

const initialStateAdminGetNewsletter = {
    loading_admin_get_newsletter: false,
    status_admin_get_newsletter: false,
    data_admin_get_newsletter: null,
    error_admin_get_newsletter: null,
};
export const AdminGetNewsletterReducer = (state = initialStateAdminGetNewsletter, action: any) => {
    switch (action.type) {
        case ADMIN_GET_NEWSLETTER_RESET:
            return {
                ...initialStateAdminGetNewsletter,
            }
        case ADMIN_GET_NEWSLETTER_REQUEST:
            return {
                ...state,
                loading_admin_get_newsletter: true,
                status_admin_get_newsletter: false,
            }
        case ADMIN_GET_NEWSLETTER_SUCCESS:
            return {
                ...state,
                loading_admin_get_newsletter: false,
                status_admin_get_newsletter: true,
                data_admin_get_newsletter: action.payload
            }
        case ADMIN_GET_NEWSLETTER_FAIL:
            return {
                ...state,
                loading_admin_get_newsletter: false,
                status_admin_get_newsletter: false,
                error_admin_get_newsletter: action.payload
            }
        default:
            return state;
    }
};

const initialStateAdminDeleteNewsletter = {
    loading_admin_delete_newsletter: false,
    status_admin_delete_newsletter: false,
    data_admin_delete_newsletter: null,
    error_admin_delete_newsletter: null,
};
export const AdminDeleteNewsletterReducer = (state = initialStateAdminDeleteNewsletter, action: any) => {
    switch (action.type) {
        case ADMIN_NEWSLETTER_DELETE_RESET:
            return {
                ...initialStateAdminDeleteNewsletter,
            }
        case ADMIN_NEWSLETTER_DELETE_REQUEST:
            return {
                ...state,
                loading_admin_delete_newsletter: true,
                status_admin_delete_newsletter: false,
            }
        case ADMIN_NEWSLETTER_DELETE_SUCCESS:
            return {
                ...state,
                loading_admin_delete_newsletter: false,
                status_admin_delete_newsletter: true,
                data_admin_delete_newsletter: action.payload
            }
        case ADMIN_NEWSLETTER_DELETE_FAIL:
            return {
                ...state,
                loading_admin_delete_newsletter: false,
                status_admin_delete_newsletter: false,
                error_admin_delete_newsletter: action.payload
            }
        default:
            return state;
    }
};
