import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { AdminAddBannerAction } from '../../Redux/Actions/BannerActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { toast } from 'react-toastify';
import { bannerTypeOption } from "../../Utils/Helper";
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';

const AddBannerForm = () => {
    const dispatch = useDispatch<any>();

    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const [bannerImage, setBannerImage] = useState("");
    const [bannerMessage, setBannerMessage] = useState("");

    const { status_admin_add_banner, data_admin_add_banner, error_admin_add_banner, loading_admin_add_banner } = useSelector((state: any) => state.AdminAddBannerState);

    const formSchema = Yup.object().shape({
        type: Yup.string()
            .required(VALIDATION_MESSAGE?.BANNER_TYPE_REQUIRED),

        position: Yup.string()
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),

        banner_image: Yup.mixed()
            .test("required", VALIDATION_MESSAGE?.BANNER_IMAGE_REQUIRED, (file) => {
                if (file) return true;
                return false;
            })
            // .test("fileSize", VALIDATION_MESSAGE?.BANNER_IMAGE_SIZE, (file) => {
            //     if (file.length > 0) {
            //         return file && file[0].size <= 2000000;
            //     }
            //     else {
            //         return true;
            //     }
            // })
            .test("fileType", VALIDATION_MESSAGE?.BANNER_UNSUPPORTED, (file) => {
                if (file.length > 0) {
                    var file_type = file.length && ["image/jpeg", "image/png", "image/jpg"].includes(file[0].type)
                    if (file_type) {
                        return file_type;
                    }
                    else {
                        setValue('banner_image', '');
                        return file_type;
                    }
                }
                else {
                    return true;
                }
            }),

        productId: Yup.string()
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),
    });

    const {
        register,
        handleSubmit,
        reset,
        setValue,
        getValues,
        formState: { errors },
        control,
    } = useForm({
        resolver: yupResolver(formSchema),
        mode: 'onChange',
        defaultValues: {
            type: "",
            position: "",
            banner_image: "",
            productId: "",
        }
    });

    const selectFileHandler = (e: any) => {
        let file = e.target.files[0];
        if (file !== '') {
            setBannerImage(file);
        }
    }

    const onSubmit = async (payload: any) => {
        delete payload['banner_image'];
        if (bannerImage != '') {
            payload['banner_image'] = bannerImage;
        }
        dispatch(AdminAddBannerAction(payload));
    };

    useEffect(() => {
        // var banner_type = bannerTypeOption().filter(option => option.value === 1);
        // setBannerMessage(banner_type[0]?.message);
        if (status_admin_add_banner) {
            toast.success(data_admin_add_banner?.message);
            dispatch(AdminAddBannerAction('RESET'));
            reset();
        }
    }, [dispatch, status_admin_add_banner]);
    return (
        <Fragment>
            {loading_admin_add_banner ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Add Banner</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "banner"}>
                                            Banner Management
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                            <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                <div className="p-4">
                                    <div className="form-row">
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Type of Banner</label>
                                            <Controller
                                                control={control}
                                                rules={{ required: VALIDATION_MESSAGE?.BANNER_TYPE_REQUIRED }}
                                                name={"type"}
                                                render={(props) => (
                                                    <Select
                                                        placeholder="Select Banner Type"
                                                        value={
                                                            bannerTypeOption().filter(option => option.value === Number(getValues('type')))
                                                        }
                                                        onChange={(e: any) => {
                                                            setValue(`type`, e?.value);
                                                            var banner_type_inner = bannerTypeOption().filter(option => option.value === e?.value);
                                                            setBannerMessage(banner_type_inner[0]?.message);
                                                        }}
                                                        options={bannerTypeOption()}
                                                        classNamePrefix="dropDown"
                                                        isMulti={false}
                                                        className="react-select"
                                                    />
                                                )}
                                            />
                                            <p className="text-danger">{errors?.type?.message ?? error_admin_add_banner?.errors?.type?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Position</label>
                                            <input
                                                type="number"
                                                placeholder="Enter Position"
                                                className="form-control"
                                                {...register('position', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 5) {
                                                            setValue(`position`, e.target.value.slice(0, 5))
                                                        }
                                                        else {
                                                            setValue(`position`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.position?.message ?? error_admin_add_banner?.errors?.position?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6 form-group-checkbox">
                                            <label className="form-label">Upload the image  {bannerMessage && bannerMessage != '' ? <span className="pixel_message text-danger"> ({bannerMessage})</span> : ""}</label>
                                            <input
                                                type="file"
                                                accept="image/jpeg,image/png"
                                                className="form-control"
                                                {...register('banner_image', {
                                                    onChange: (e) => { selectFileHandler(e) }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.banner_image?.message ?? error_admin_add_banner?.errors?.banner_image?.msg}</p>
                                        </div>
                                        <div className="form-group col-md-6">
                                            <label className="form-label">Product Id</label>
                                            <input
                                                type="number"
                                                placeholder="Enter Product Id"
                                                className="form-control"
                                                {...register('productId', {
                                                    onChange: (e) => {
                                                        if (e.target.value.length >= 10) {
                                                            setValue(`productId`, e.target.value.slice(0, 10))
                                                        }
                                                        else {
                                                            setValue(`productId`, e.target.value)
                                                        }
                                                    }
                                                })}
                                            />
                                            <p className="text-danger">{errors?.productId?.message ?? error_admin_add_banner?.errors?.productId?.msg}</p>
                                        </div>
                                    </div>
                                    <div className="mt-3 text-center">
                                        <button type="submit" className="btn btn-primary">Save</button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default AddBannerForm;
