import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import { Link } from 'react-router-dom';
import { AdminAddUserAction } from '../../../Redux/Actions/UserActions';
import { VALIDATION_MESSAGE } from '../../../Constants/Constants';
// import { statusOption } from "../../../Utils/Helper";
// import { REACT_APP_BASE_URL } from '../../../Config/Config';

import { useForm } from 'react-hook-form';
// import { useForm, Controller } from 'react-hook-form';
// import Select from "react-select";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';

import { toast } from 'react-toastify';

import Header from '../../../Layouts/Header';
import Sidebar from '../../../Layouts/Sidebar';
import Footer from '../../../Layouts/Footer';
import Spinner from '../../../Layouts/Spinner';
import { getIpAddress } from "../../../Utils/Helper";

const AddBuyerForm = () => {
    const dispatch = useDispatch<any>();

    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const { status_admin_add_user, data_admin_add_user, error_admin_add_user, loading_admin_add_user } = useSelector((state: any) => state.AdminAddUserState);

    // const [passwordType, setPasswordType] = useState(false);
    // const [confirmPasswordType, setConfirmPasswordType] = useState(false);

    const formSchema = Yup.object().shape({
        full_name: Yup.string()
            .required(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.FULL_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.FULL_NAME_VALID)
            .max(25, VALIDATION_MESSAGE?.FULL_NAME_MAX),

        company_name: Yup.string()
            .required(VALIDATION_MESSAGE?.COMPANY_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.COMPANY_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.COMPANY_NAME_VALID)
            .max(25, VALIDATION_MESSAGE?.COMPANY_NAME_MAX),

        email: Yup.string()
            .required(VALIDATION_MESSAGE?.EMAIL_REQUIRED)
            .email(VALIDATION_MESSAGE?.VALID_EMAIL)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),

        mobile_number: Yup.string()
            .required(VALIDATION_MESSAGE?.MOBILE_NUMBER_REQUIRED)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(new RegExp('[0-9]{9}'), VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/, VALIDATION_MESSAGE?.PHONE_NUMBER_VALID)
            .min(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MIN)
            .max(10, VALIDATION_MESSAGE?.MOBILE_NUMBER_MAX),

        pan_card_number: Yup.string()
            .required(VALIDATION_MESSAGE?.PAN_CARD_NUMBER_REQUIRED)
            .min(10, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID)
            .max(10, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(/^([a-zA-Z]([a-zA-Z]([a-zA-Z]([a-zA-Z]([a-zA-Z]([0-9]([0-9]([0-9]([0-9]([a-zA-Z])?)?)?)?)?)?)?)?)?)?$/, VALIDATION_MESSAGE?.PAN_CARD_NUMBER_VALID),

        // password: Yup.string()
        //     .required(VALIDATION_MESSAGE?.PASSWORD_REQUIRED)
