import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2'

import { AdminGetProductAction, AdminChangeStatusProductAction, AdminDeleteProductAction, AdminGetByIdProductAction } from '../../Redux/Actions/ProductActions';
import { AdminGetParentCategoryAction } from '../../Redux/Actions/CategoryActions';
import { REACT_APP_BASE_URL, REACT_PAGINATE_LIMIT, REACT_PAGINATE_NEXT_LABEL, REACT_PAGINATE_PREVIOUS_LABEL, REACT_PAGINATE_BREAK_LABEL } from '../../Config/Config';
import ReactPaginate from 'react-paginate';
import { toast } from 'react-toastify';
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";

import { productStatusOption, encodeID } from "../../Utils/Helper";
import { VALIDATION_MESSAGE } from '../../Constants/Constants';

import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';
const Product = () => {
    const dispatch = useDispatch<any>();
    const navigate = useNavigate();
    const [paginateLimit, setPaginateLimit] = useState(REACT_PAGINATE_LIMIT);
    const [paginatePage, setPaginatePage] = useState(1);
    const [search, setSearch] = useState('');
    const [ProductStatus, setProductStatus] = useState(404);
    const [paginateCount, setPaginateCount] = useState(0);
    const [categoryParentId, setCategoryParentId] = useState(404);
    const [getParentCategory, setParentCategory] = useState<any[]>([]);
    const [categoryChildId, setCategoryChildId] = useState(404);
    const [getChildCategory, setChildCategory] = useState<any[]>([]);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_get_parent_category, data_admin_get_parent_category, error_admin_get_parent_category, loading_admin_get_parent_category } = useSelector((state: any) => state.AdminGetParentCategoryState);
    const { status_admin_get_product, data_admin_get_product, error_admin_get_product, loading_admin_get_product } = useSelector((state: any) => state.AdminGetProductState);
    const { status_admin_change_status_product, data_admin_change_status_product, error_admin_change_status_product, loading_admin_change_status_product } = useSelector((state: any) => state.AdminChangeStatusProductState);
    const { status_admin_delete_product, data_admin_delete_product, error_admin_delete_product, loading_admin_delete_product } = useSelector((state: any) => state.AdminDeleteProductState);

    const {
        control,
    } = useForm({});

    const confirmStatusChange = async (id: number, status: number) => {
        var text_message = '';
        if (status === 0) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_PRODUCT_DISABLE;
        }
        else if (status === 1) {
            text_message = VALIDATION_MESSAGE?.ARE_YOU_STATUS_PRODUCT_ENABLE;
        }
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: text_message,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                statusChange(id, status);
            }
        });
    };

    const statusChange = async (id: number, status: number) => {
        const params = {
            "id": id,
            "status": status
        }
        dispatch(AdminChangeStatusProductAction(params));
    };

    const confirmDelete = async (delete_id: number) => {
        Swal.fire({
            title: VALIDATION_MESSAGE?.ARE_YOU_SURE,
            text: VALIDATION_MESSAGE?.ARE_YOU_DELETE_PRODUCT,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#31CE36',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Ok'
        }).then((result) => {
            if (result.isConfirmed) {
                deleteData(delete_id);
            }
        });
    };

    const deleteData = async (delete_id: number) => {
        const params = {
            "delete_id": delete_id
        }
        dispatch(AdminDeleteProductAction(params));
    };

    const editData = async (user_id: number) => {
        dispatch(AdminGetByIdProductAction('RESET'));
        navigate(REACT_APP_BASE_URL + 'product-edit/' + encodeID(user_id), { replace: true })
    };

    const handlePaginateClick = (event: any) => {
        var next_page_set = event.selected + 1;
        setPaginatePage(next_page_set);
        const params = {
            "role_id": 2,
            "search": search,
            "status": ProductStatus,
            "page": next_page_set,
            "limit": paginateLimit
        }
        dispatch(AdminGetProductAction(params));
    };

    const handleSearch = (event: any) => {
        if (event.keyCode === 13) {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "role_id": 2,
                "search": event.target.value,
                "status": ProductStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetProductAction(params));
        }
        else if (event.target.value === '') {
            setPaginatePage(1);
            setSearch(event.target.value);
            const params = {
                "role_id": 2,
                "search": event.target.value,
                "status": ProductStatus,
                "page": 1,
                "limit": paginateLimit
            }
            dispatch(AdminGetProductAction(params));
        }
    }   

    const categoryParentIdFilltr = (parentId: any) => {
        // setPaginatePage(1);
        // setCategoryParentId(parentId);
        // const params = {
        //     "search": search,
        //     "parentId": parentId,
        //     "status": categoryStatus,
        //     "page": 1,
        //     "limit": paginateLimit
        // }
        // dispatch(AdminGetProductAction(params));
    }

    const categoryChildIdFilltr = (parentId: any) => {
        // setPaginatePage(1);
        // setCategoryParentId(parentId);
        // const params = {
        //     "search": search,
        //     "parentId": parentId,
        //     "status": categoryStatus,
        //     "page": 1,
        //     "limit": paginateLimit
        // }
        // dispatch(AdminGetProductAction(params));
    }

    const productStatusFilltr = (status: any) => {
        setPaginatePage(1);
        setProductStatus(status);
        const params = {
            "role_id": 2,
            "search": search,
            "status": status,
            "page": 1,
            "limit": paginateLimit
        }
        dispatch(AdminGetProductAction(params));
    }

    useEffect(() => {
        dispatch(AdminGetParentCategoryAction({}));
        const params = {
            "role_id": 2,
            "search": search,
            "status": ProductStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        dispatch(AdminGetProductAction(params));
    }, []);

    useEffect(() => {
        const params = {
            "role_id": 2,
            "search": search,
            "status": ProductStatus,
            "page": paginatePage,
            "limit": paginateLimit
        }
        if (status_admin_get_parent_category) {
            if (data_admin_get_parent_category && data_admin_get_parent_category?.status === true && data_admin_get_parent_category?.data?.length > 0) {
                var get_parent_category: any[] = [];
                get_parent_category.push({
                    label: "Select Parent Category",
                    value: 0,
                });
                data_admin_get_parent_category?.data?.map((d: any, i: any) => {
                    get_parent_category.push({
                        label: d?.name,
                        value: d?.id,
                    });
                });
                setParentCategory(get_parent_category);
            }
        }
        if (status_admin_change_status_product) {
            dispatch(AdminGetProductAction(params));
            toast.success(data_admin_change_status_product?.message);
            dispatch(AdminChangeStatusProductAction('RESET'));
        }
        if (status_admin_delete_product) {
            dispatch(AdminGetProductAction(params));
            toast.success(data_admin_delete_product?.message);
            dispatch(AdminDeleteProductAction('RESET'));
        }
        setPaginateCount(Math.ceil(data_admin_get_product?.data?.count / paginateLimit));
    }, [dispatch, status_admin_get_parent_category, data_admin_get_product, status_admin_change_status_product, status_admin_delete_product]);
    return (
        <Fragment>
            {loading_admin_get_product ? <Spinner /> : ""}
            {loading_admin_change_status_product ? <Spinner /> : ""}
            {loading_admin_delete_product ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Product Management</h4> 
                            </div>
                            <div className="row align-items-center mb-3">
                                <div className="col">
                                    <div className="col-lg-12 col-md-12">
                                        <div className="row">
                                            <div className="col-lg-3 col-md-4 pl-3">
                                                <div className="input-group">
                                                    <input type="text" className="form-control" onKeyDown={handleSearch} onChange={handleSearch} placeholder="Search ..." />
                                                </div>
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Parent Category"
                                                            onChange={(e: any) => {
                                                                categoryParentIdFilltr(e?.value);
                                                            }}
                                                            options={getParentCategory}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Child Category"
                                                            onChange={(e: any) => {
                                                                categoryChildIdFilltr(e?.value);
                                                            }}
                                                            options={getChildCategory}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                            <div className="col-lg-3 col-md-3">
                                                <Controller
                                                    control={control}
                                                    name={"status"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Product Status"
                                                            onChange={(e: any) => {
                                                                productStatusFilltr(e?.value);
                                                            }}
                                                            options={productStatusOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-auto">
                                    <Link to={REACT_APP_BASE_URL + "product-create"} className="btn btn-primary ml-2">
                                        Create Product
                                    </Link>
                                </div>
                            </div>
                            <div className="card listing_c order_managment card-list">
                                <div className="row align-items-center">
                                    <div className="col-lg-1 col-md-1">
                                        <h6 className="fw-bold mb-1">S No</h6>
                                    </div>
                                    <div className="col-lg-7 col-md-7">
                                        <h6 className="fw-bold mb-1">Name of product</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Status</h6>
                                    </div>
                                    <div className="col-lg-2 col-md-2  text-center">
                                        <h6 className="fw-bold mb-1"> Action</h6>
                                    </div>
                                </div>
                                {data_admin_get_product?.data?.rows?.length > 0 ?
                                    data_admin_get_product?.data?.rows.map((d: any, i: any) => {
                                        var get_status = productStatusOption().filter(option => option.value === d?.status);
                                        return (
                                            <div className="row align-items-center" key={i}>
                                                <div className="col-lg-1 col-md-1  text-center">
                                                    <div className="pro_name">
                                                        {/* <img src={String(ImageHelper.AvatarImage)} alt="..." className="avatar-img rounded-circle" /> */}
                                                        {(paginatePage - 1) * paginateLimit + i + 1}
                                                    </div>
                                                </div>
                                                <div className="col-lg-7 col-md-7">
                                                    <div className="list_left">
                                                        <div className="pro_name">
                                                            {d?.name}
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="pro_name">
                                                        {get_status[0]?.label}
                                                    </div>
                                                </div>
                                                <div className="col-lg-2 col-md-2 text-center">
                                                    <div className="list_right">
                                                        <span onClick={() => { editData(d?.id); }}>
                                                            <i className="fas fa-edit" title="Edit"></i>
                                                        </span>
                                                        <span onClick={() => { confirmDelete(d?.id); }}>
                                                            <i className="fas fa-trash-alt" title="Delete"></i>
                                                        </span>
                                                        {(() => {
                                                            if (d?.status === 1) {
                                                                return (
                                                                    <span onClick={() => { confirmStatusChange(d?.id, 0); }}>
                                                                        <i className="fas fa-ban" title="Disable"></i>
                                                                    </span>
                                                                )
                                                            } else if (d?.status === 0) {
                                                                return (
                                                                    <span onClick={() => { confirmStatusChange(d?.id, 1); }}>
                                                                        <i className="fas fa-check" title="Enable"></i>
                                                                    </span>
                                                                )
                                                            }
                                                        })()}
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    })
                                    : "No data found."}
                            </div>
                            {paginateCount > 0 ?
                                <div className="col-sm-12 react-paginate cutome-react-paginate justify-content-center">
                                    <ReactPaginate
                                        className="pagination justify-content-center"
                                        breakLabel={REACT_PAGINATE_BREAK_LABEL}
                                        nextLabel={REACT_PAGINATE_NEXT_LABEL}
                                        previousLabel={REACT_PAGINATE_PREVIOUS_LABEL}
                                        pageRangeDisplayed={paginateLimit}
                                        onPageChange={handlePaginateClick}
                                        pageCount={paginateCount}
                                        breakClassName={'page-item'}
                                        breakLinkClassName={'page-link'}
                                        containerClassName={'pagination'}
                                        pageClassName={'page-item'}
                                        pageLinkClassName={'page-link'}
                                        previousClassName={'page-item'}
                                        previousLinkClassName={'page-link'}
                                        nextClassName={'page-item'}
                                        nextLinkClassName={'page-link'}
                                        activeClassName={'active'}
                                    />
                                </div>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment >
    );
}

export default Product;
