import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { AdminEditBannerAction, AdminGetByIdBannerAction } from '../../Redux/Actions/BannerActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import { bannerTypeOption, getLastURL, decodeID } from "../../Utils/Helper";
import { useForm, Controller } from 'react-hook-form';
import Select from "react-select";
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { toast } from 'react-toastify';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';

const EditBannerForm = () => {
    const dispatch = useDispatch<any>();
    const location = useLocation();
    const editId = getLastURL(location);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    const [bannerImage, setBannerImage] = useState("");

    const { status_admin_edit_banner, data_admin_edit_banner, error_admin_edit_banner, loading_admin_edit_banner } = useSelector((state: any) => state.AdminEditBannerState);
    const { status_admin_get_by_id_banner, data_admin_get_by_id_banner, error_admin_get_by_id_banner, loading_admin_get_by_id_banner } = useSelector((state: any) => state.AdminGetByIdBannerState);

    const formSchema = Yup.object().shape({
        type: Yup.string()
            .required(VALIDATION_MESSAGE?.BANNER_TYPE_REQUIRED),

        position: Yup.string()
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),

        banner_image: Yup.mixed()
            // .test("fileSize", VALIDATION_MESSAGE?.BANNER_IMAGE_SIZE, (file) => {
            //     if (file.length > 0) {
            //         return file && file[0].size <= 2000000;
            //     }
            //     else {
            //         return true;
            //     }
            // })
            .test("fileType", VALIDATION_MESSAGE?.CATEGORY_UNSUPPORTED, (file) => {
                if (file.length > 0) {
                    var file_type = file.length && ["image/jpeg", "image/png", "image/jpg"].includes(file[0].type)
                    if (file_type) {
                        return file_type;
                    }
                    else {
                        setValue('banner_image', '');
                        return file_type;
                    }
                }
                else {
                    return true;
                }
            }),

        productId: Yup.string()
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED),
    });

    const {
        register,
        handleSubmit,
        setValue,
        getValues,
        formState: { errors },
        control,
    } = useForm({
        resolver: yupResolver(formSchema),
        mode: 'onChange',
        defaultValues: {
            id: decodeID(editId),
            title: "",
            type: 0,
            position: "",
            banner_image: "",
            banner_image_full: "",
            productId: "",
        }
    });

    const selectFileHandler = (e: any) => {
        let file = e.target.files[0];
        if (file !== '') {
            setBannerImage(file);
        }
    }

    const onSubmit = async (payload: any) => {
        delete payload['title'];
        delete payload['banner_image'];
        delete payload['banner_image_full'];
        if (bannerImage != '') {
            payload['banner_image'] = bannerImage;
        }
        dispatch(AdminEditBannerAction(payload));
    };

    const set_Value = async (data: any) => {
        setValue("title", data?.title);
        setValue("type", data?.type);
        setValue("position", data?.position);
        setValue("productId", data?.productId);
        setValue("banner_image_full", data?.banner_image_full);
    };

    useEffect(() => {
        if (status_admin_get_by_id_banner) {
            set_Value(data_admin_get_by_id_banner?.data);
        }
        else {
            var params = {
                id: decodeID(editId)
            };
            dispatch(AdminGetByIdBannerAction(params));
        }
    }, [status_admin_get_by_id_banner]);

    useEffect(() => {
        if (status_admin_edit_banner) {
            toast.success(data_admin_edit_banner?.message);
            set_Value(data_admin_edit_banner?.data);
            dispatch(AdminEditBannerAction('RESET'));
        }
    }, [status_admin_edit_banner]);
    return (
        <Fragment>
            {loading_admin_get_by_id_banner || loading_admin_edit_banner ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Edit Banner</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "banner"}>
                                            Banner Management
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                            {status_admin_get_by_id_banner ?
                                <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                    <div className="p-4">
                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Name of Banner</label>
                                                <input
                                                    type="text"
                                                    placeholder="Enter Name Of Banner"
                                                    className="form-control"
                                                    {...register('title', { disabled: true })}
                                                />
                                                <p className="text-danger">{errors?.title?.message ?? error_admin_edit_banner?.errors?.title?.msg}</p>
                                            </div>
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Type of Banner</label>
                                                <Controller
                                                    control={control}
                                                    rules={{ required: VALIDATION_MESSAGE?.BANNER_TYPE_REQUIRED }}
                                                    name={"type"}
                                                    render={(props) => (
                                                        <Select
                                                            placeholder="Select Banner Type"
                                                            value={
                                                                bannerTypeOption().filter(option => option.value === Number(getValues('type')))
                                                            }
                                                            onChange={(e: any) => {
                                                                setValue(`type`, e?.value);
                                                            }}
                                                            options={bannerTypeOption()}
                                                            classNamePrefix="dropDown"
                                                            isMulti={false}
                                                            className="react-select"
                                                        />
                                                    )}
                                                />
                                                <p className="text-danger">{errors?.type?.message ?? error_admin_edit_banner?.errors?.type?.msg}</p>
                                            </div>
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Position</label>
                                                <input
                                                    type="number"
                                                    placeholder="Enter Position"
                                                    className="form-control"
                                                    {...register('position', {
                                                        onChange: (e) => {
                                                            if (e.target.value.length >= 5) {
                                                                setValue(`position`, e.target.value.slice(0, 5))
                                                            }
                                                            else {
                                                                setValue(`position`, e.target.value)
                                                            }
                                                        }
                                                    })}
                                                />
                                                <p className="text-danger">{errors?.position?.message ?? error_admin_edit_banner?.errors?.position?.msg}</p>
                                            </div>                                            
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Product Id</label>
                                                <input
                                                    type="number"
                                                    placeholder="Enter Product Id"
                                                    className="form-control"
                                                    {...register('productId', {
                                                        onChange: (e) => {
                                                            if (e.target.value.length >= 10) {
                                                                setValue(`productId`, e.target.value.slice(0, 10))
                                                            }
                                                            else {
                                                                setValue(`productId`, e.target.value)
                                                            }
                                                        }
                                                    })}
                                                />
                                                <p className="text-danger">{errors?.productId?.message ?? error_admin_edit_banner?.errors?.productId?.msg}</p>
                                            </div>
                                            <div className="form-group col-md-6 form-group-checkbox">
                                                <label className="form-label">Upload the image</label>
                                                {(() => {
                                                    if (getValues('banner_image_full') && getValues('banner_image_full') != null) {
                                                        return (
                                                            <div className="form_under_image">
                                                                <img alt="Image" src={getValues('banner_image_full')} />
                                                            </div>
                                                        );
                                                    }
                                                })()}
                                                <input
                                                    type="file"
                                                    accept="image/jpeg,image/png"
                                                    className="form-control"
                                                    {...register('banner_image', {
                                                        onChange: (e) => { selectFileHandler(e) }
                                                    })}
                                                />
                                                <p className="text-danger">{errors?.banner_image?.message ?? error_admin_edit_banner?.errors?.banner_image?.msg}</p>
                                            </div>
                                        </div>
                                        <div className="mt-3 text-center">
                                            <button type="submit" className="btn btn-primary">Save</button>
                                        </div>
                                    </div>
                                </form>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default EditBannerForm;
