import React, { Fragment, useState, useEffect } from 'react';
import { REACT_APP_BASE_URL } from '../Config/Config';
import { Link, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import ImageHelper from '../Utils/imageHelper';
const Sidebar = () => {
	const location = useLocation();
	const current_page = location?.pathname;
	const [showProfileBox, setshowProfileBox] = useState(false);
	const [showSubMenu, setshowSubMenu] = useState(0);
	const handleLogout = () => {
		localStorage.clear();
		sessionStorage.clear()
		window.location.href = REACT_APP_BASE_URL + '/login'
	}
	const { data_get_profile } = useSelector((state: any) => state.AdminGetProfileState);
	var IsMenuActive = 0;
	var IsSubMenuActive = 0;
	if (current_page === "") {
		IsMenuActive = 1;
	}
	else if (current_page === REACT_APP_BASE_URL) {
		IsMenuActive = 1;
	}
	else if (current_page === REACT_APP_BASE_URL + "banner" || current_page === REACT_APP_BASE_URL + "banner-create" || current_page === REACT_APP_BASE_URL + "banner-edit") {
		IsMenuActive = 2;
	}
	else if (current_page === REACT_APP_BASE_URL + "category" || current_page === REACT_APP_BASE_URL + "category-create" || current_page === REACT_APP_BASE_URL + "category-edit") {
		IsMenuActive = 3;
	}
	else if (current_page === REACT_APP_BASE_URL + "product" || current_page === REACT_APP_BASE_URL + "product-create" || current_page === REACT_APP_BASE_URL + "product-edit") {
		IsMenuActive = 4;
	}
	else if (current_page === REACT_APP_BASE_URL + "buyer" || current_page === REACT_APP_BASE_URL + "buyer-create" || current_page === REACT_APP_BASE_URL + "buyer-view") {
		IsMenuActive = 5;
		IsSubMenuActive = 501;
	}
	else if (current_page === REACT_APP_BASE_URL + "distributor" || current_page === REACT_APP_BASE_URL + "distributor-create" || current_page === REACT_APP_BASE_URL + "distributor-view") {
		IsMenuActive = 5;
		IsSubMenuActive = 502;
	}
	const Menus = [
		{
			Name: "Dashboard",
			Route: REACT_APP_BASE_URL,
			Icon: "fas fa-tachometer-alt",
			IsMenuId: 1,
			IsMenuActive: IsMenuActive,
			IsSubMenu: false,
			Module: "dashboard",
		},
		{
			Name: "Banner Management",
			Route: REACT_APP_BASE_URL + "banner",
			Icon: "fas fa-ticket-alt",
			IsMenuId: 2,
			IsMenuActive: IsMenuActive,
			IsSubMenu: false,
			Module: "banner_management",
		},
		{
			Name: "Category Management",
			Route: REACT_APP_BASE_URL + "category",
			Icon: "fas fa-layer-group",
			IsMenuId: 3,
			IsMenuActive: IsMenuActive,
			IsSubMenu: false,
			Module: "category_management",
		},
		{
			Name: "Product Management",
			Route: REACT_APP_BASE_URL + "product",
			Icon: "fas fa-desktop",
			IsMenuId: 4,
			IsMenuActive: IsMenuActive,
			IsSubMenu: false,
			Module: "product_management",
		},
		{
			Name: "User Management",
			Route: "#",
			Icon: "fa-user",
			IsMenuId: 5,
			IsMenuActive: IsMenuActive,
			IsSubMenu: true,
			ShowRole: "Admin",
			SubMenu: [
				{
					Name: "Buyer Management.",
					Route: REACT_APP_BASE_URL + "buyer",
					Count: "",
					IsMenuId: 501,
					IsSubMenuActive: IsSubMenuActive,
				},
				{
					Name: "Distributor Management.",
					Route: REACT_APP_BASE_URL + "distributor",
					Count: "",
					IsMenuId: 502,
					IsSubMenuActive: IsSubMenuActive,
				},
			],
		},
	];
	const onMenuClick = (i: any) => {
		if (Number(showSubMenu) === Number(i)) {
			setshowSubMenu(Number(0));
		} else {
			setshowSubMenu(Number(i));
		}
	};
	useEffect(() => {
	}, [showSubMenu]);
	return (
		<Fragment>
			<div className="sidebar sidebar-style-2">
				<div className="sidebar-wrapper scrollbar scrollbar-inner">
					<div className="sidebar-content">
						<div className="user">
							<div className="avatar-sm float-left mr-2">
								<img src={data_get_profile?.profile_image_full ? data_get_profile?.profile_image_full : String(ImageHelper.AvatarImage)} alt="..." className="avatar-img rounded-circle" />
							</div>
							<div className="info">
								<Link data-toggle="collapse" to={"#"} onClick={() => { setshowProfileBox(!showProfileBox); }}>
									<span>
										{/* {data_get_profile?.full_name} */}
										<span className="user-level">{data_get_profile?.full_name}</span>
										{/* <span className="caret"></span> */}
									</span>
								</Link>
								<div className="clearfix"></div>
								{/* <div className={`collapse in ${showProfileBox ? 'show' : ''}`}>
									<ul className="nav">
										<li>
											<Link to={REACT_APP_BASE_URL + "profile"}>
												<span className="link-collapse">My Profile</span>
											</Link>
										</li>
										<li>
											<Link to={REACT_APP_BASE_URL + "change-password"}>
												<span className="link-collapse">Change Password</span>
											</Link>
										</li>
										<li>
											<Link to="#" onClick={handleLogout}>
												<span className="link-collapse">Logout</span>
											</Link>
										</li>
									</ul>
								</div> */}
							</div>
						</div>
						{Menus.length > 0 && (
							<Fragment>
								<ul className="nav nav-primary">
									{Menus.map((m, i) => {
										if (m.IsSubMenu && typeof m?.SubMenu !== "undefined" && m?.SubMenu.length > 0) {
											var isActive = false;
											if (Number(showSubMenu) === Number(m.IsMenuId)) {
												isActive = true;
											}
											if (Number(m.IsMenuId) === Number(m.IsMenuActive)) {
												isActive = true;
											}
											return (
												<li key={i} className={`nav-item ${isActive ? "active show submenu" : ""}`}>
													<Link
														data-toggle={`${isActive ? "" : "collapse"}`}
														to={`#sub_menu_${i}`}
														aria-expanded={isActive}
														onClick={() => {
															onMenuClick(m.IsMenuId);
														}}
													>
														<i className={`fas ${m?.Icon}`}></i>
														<p>{m.Name}</p>
														<span className="caret"></span>
													</Link>
													<div className={`collapse ${isActive ? "show" : ""}`} id={`sub_menu_${i}`}>
														<ul className="nav nav-collapse">
															{m?.SubMenu.map((ms, is) => {
																var isSubActive = false;
																if (Number(ms.IsMenuId) === Number(ms.IsSubMenuActive)) {
																	isSubActive = true;
																}
																return (
																	<li className={`${isSubActive ? "active" : ""}`} key={is}>
																		<Link to={ms.Route}>
																			<span className="sub-item">{ms.Name}</span>
																		</Link>
																	</li>
																)
															})}
														</ul>
													</div>
												</li>
											);
										}
										else {
											var isParentActive = false;
											if (Number(m.IsMenuId) === Number(m.IsMenuActive)) {
												isParentActive = true;
											}
											return (
												<li className={`nav-item ${isParentActive ? "active" : ""}`} key={i}>
													<Link to={m.Route}>
														<i className={`fas ${m?.Icon}`}></i>
														<p>{m.Name}</p>
													</Link>
												</li>
											);
										}
									})};
								</ul>
							</Fragment>
						)};
					</div>
				</div>
			</div>
		</Fragment>
	);
}
export default Sidebar;
