import React, { Fragment, useState, useEffect } from 'react';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { AdminChangePasswordAction } from '../../Redux/Actions/AuthActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import Spinner from '../../Layouts/Spinner';
function ChangePassword() {
    const dispatch = useDispatch<any>();
    const formSchema = Yup.object().shape({
        old_password: Yup.string()
            .required(VALIDATION_MESSAGE?.OLD_PASSWORD_REQUIRED)
            .min(8, VALIDATION_MESSAGE?.OLD_PASSWORD_MIN_CHAR)
            .max(16, VALIDATION_MESSAGE?.OLD_PASSWORD_MAX_CHAR)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(/^(?=.*[A-Z])/, VALIDATION_MESSAGE?.PASSWORD_UPPER_CASE)
            // .matches(/^(?=.*[!@#\$%\^&\*])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
            .matches(/^(?=.*[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
            .matches(/^(?=.{6,20}$)\D*\d/, VALIDATION_MESSAGE?.PASSWORD_NUMBER_CASE),

        password: Yup.string()
            .required(VALIDATION_MESSAGE?.NEW_PASSWORD_REQUIRED)
            .min(8, VALIDATION_MESSAGE?.NEW_PASSWORD_MIN_CHAR)
            .max(16, VALIDATION_MESSAGE?.NEW_PASSWORD_MAX_CHAR)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(/^(?=.*[A-Z])/, VALIDATION_MESSAGE?.PASSWORD_UPPER_CASE)
            .matches(/^(?=.*[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
            .matches(/^(?=.{6,20}$)\D*\d/, VALIDATION_MESSAGE?.PASSWORD_NUMBER_CASE),

        confirm_password: Yup.string()
            .required(VALIDATION_MESSAGE?.CONFIRM_NEW_PASSWORD_REQUIRED)
            .min(8, VALIDATION_MESSAGE?.CONFIRM_NEW_PASSWORD_MIN_CHAR)
            .max(16, VALIDATION_MESSAGE?.CONFIRM_NEW_PASSWORD_MAX_CHAR)
            .matches(/^\S*$/, VALIDATION_MESSAGE?.NO_SPACE_ALLOWED)
            .matches(/^(?=.*[A-Z])/, VALIDATION_MESSAGE?.PASSWORD_UPPER_CASE)
            .matches(/^(?=.*[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~])/, VALIDATION_MESSAGE?.PASSWORD_SPECIAL_CASE)
            .matches(/^(?=.{6,20}$)\D*\d/, VALIDATION_MESSAGE?.PASSWORD_NUMBER_CASE)
            .oneOf([Yup.ref('password')], VALIDATION_MESSAGE?.PASSWORD_CPWD_NOT_MATCHED),
    })
    const formOptions = { resolver: yupResolver(formSchema) }
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
        defaultValues: {
            old_password: "",
            password: "",
            confirm_password: ""
        }
    });
    const [oldPasswordType, setOldPasswordType] = useState(false);
    const [passwordType, setPasswordType] = useState(false);
    const [confirmPasswordType, setConfirmPasswordType] = useState(false);
    const { status_change_password, error_change_password, loading_change_password } = useSelector((state: any) => state.AdminChangePasswordState);
    const onSubmit = async (payload: any) => {
        dispatch(AdminChangePasswordAction(payload));
    };
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);
    useEffect(() => {
        if (status_change_password) {
            dispatch(AdminChangePasswordAction('RESET'));
            reset();
        }
    }, [dispatch, status_change_password])
    return (
        <Fragment>
            {loading_change_password ? <Spinner />:""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="panel-header bg-primary-gradient">
                            <div className="page-inner py-3">
                                <div className="d-flex align-items-left align-items-md-center flex-column flex-md-row">
                                    <div>
                                        <h2 className="text-white mb-0 fw-bold">Change Password</h2>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <form onSubmit={handleSubmit(onSubmit)} className="card change_password">
                            <div className=" p-4">
                                <div className="form-group change_password_form">
                                    <label htmlFor="old_password">Old Password</label>
                                    <input
                                        type={oldPasswordType ? "text" : "password"}
                                        className="form-control"
                                        id="old_password"
                                        placeholder="Password"
                                        minLength={8}
                                        maxLength={16}
                                        {...register('old_password')}
                                    />
                                    <div className="show-password">
                                        <span className={`${oldPasswordType ? "fas fa-eye-slash" : "fas fa-eye"}`} onClick={() => { setOldPasswordType(!oldPasswordType); }}></span>
                                    </div>
                                    <p className="text-danger">{errors?.old_password?.message ?? error_change_password?.errors?.old_password?.msg}</p>
                                </div>
                                <div className="form-group change_password_form">
                                    <label htmlFor="password">Password</label>
                                    <input
                                        type={passwordType ? "text" : "password"}
                                        className="form-control"
                                        id="password"
                                        placeholder="Password"
                                        minLength={8}
                                        maxLength={16}
                                        {...register('password')}
                                    />
                                    <div className="show-password">
                                        <span className={`${passwordType ? "fas fa-eye-slash" : "fas fa-eye"}`} onClick={() => { setPasswordType(!passwordType); }}></span>
                                    </div>
                                    <p className="text-danger">{errors?.password?.message ?? error_change_password?.errors?.password?.msg}</p>
                                </div>
                                <div className="form-group change_password_form">
                                    <label htmlFor="confirm_password">Confirm Password</label>
                                    <input
                                        type={confirmPasswordType ? "text" : "password"}
                                        className="form-control"
                                        id="confirm_password"
                                        placeholder="Confirm Password"
                                        minLength={8}
                                        maxLength={16}
                                        {...register('confirm_password')}
                                    />
                                    <div className="show-password">
                                        <span className={`${confirmPasswordType ? "fas fa-eye-slash" : "fas fa-eye"}`} onClick={() => { setConfirmPasswordType(!confirmPasswordType); }}></span>
                                    </div>
                                    <p className="text-danger">{errors?.confirm_password?.message ?? error_change_password?.errors?.confirm_password?.msg}</p>
                                </div>
                                <button type="submit" className="btn btn-primary">Change</button>
                            </div>
                        </form>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}
export default ChangePassword;
