import React, { Fragment, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { AdminEditManufacturerAction, AdminGetByIdManufacturerAction } from '../../Redux/Actions/ManufacturerActions';
import { VALIDATION_MESSAGE } from '../../Constants/Constants';
import { getLastURL, decodeID } from "../../Utils/Helper";
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { toast } from 'react-toastify';
import { REACT_APP_BASE_URL } from '../../Config/Config';
import Header from '../../Layouts/Header';
import Sidebar from '../../Layouts/Sidebar';
import Footer from '../../Layouts/Footer';
import Spinner from '../../Layouts/Spinner';

const EditManufacturerForm = () => {
    const dispatch = useDispatch<any>();
    const location = useLocation();
    const editId = getLastURL(location);
    const { status_show_sidebar } = useSelector((state: any) => state.AdminSidebarState);
    const { status_show_sidebar_vertical } = useSelector((state: any) => state.AdminSidebarVerticalState);

    const { status_admin_edit_manufacturer, data_admin_edit_manufacturer, error_admin_edit_manufacturer, loading_admin_edit_manufacturer } = useSelector((state: any) => state.AdminEditManufacturerState);
    const { status_admin_get_by_id_manufacturer, data_admin_get_by_id_manufacturer, error_admin_get_by_id_manufacturer, loading_admin_get_by_id_manufacturer } = useSelector((state: any) => state.AdminGetByIdManufacturerState);

    const formSchema = Yup.object().shape({
        name: Yup.string()
            .required(VALIDATION_MESSAGE?.MANUFACTURER_NAME_REQUIRED)
            .trim(VALIDATION_MESSAGE?.MANUFACTURER_NAME_REQUIRED)
            .matches(new RegExp('^[a-zA-Z ]+$'), VALIDATION_MESSAGE?.MANUFACTURER_NAME_VALID)
            .max(30, VALIDATION_MESSAGE?.MANUFACTURER_NAME_MAX),

        hyperlink: Yup.string()
            .required(VALIDATION_MESSAGE?.MANUFACTURER_HYPERLINK_REQUIRED)
            .trim(VALIDATION_MESSAGE?.MANUFACTURER_HYPERLINK_REQUIRED)
            .matches(new RegExp('[Hh][Tt][Tt][Pp][Ss]?:\/\/(?:(?:[a-zA-Z\u00a1-\uffff0-9]+-?)*[a-zA-Z\u00a1-\uffff0-9]+)(?:\.(?:[a-zA-Z\u00a1-\uffff0-9]+-?)*[a-zA-Z\u00a1-\uffff0-9]+)*(?:\.(?:[a-zA-Z\u00a1-\uffff]{2,}))(?::\d{2,5})?(?:\/[^\s]*)?'), VALIDATION_MESSAGE?.MANUFACTURER_HYPERLINK_VALID),
    });

    const {
        register,
        handleSubmit,
        setValue,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
        mode: 'onChange',
        defaultValues: {
            id: decodeID(editId),
            name: "",
            hyperlink: "",
        }
    });

    const onSubmit = async (payload: any) => {
        dispatch(AdminEditManufacturerAction(payload));
    };

    const set_Value = async (data: any) => {
        setValue("name", data?.name);
        setValue("hyperlink", data?.hyperlink);
    };

    useEffect(() => {
        if (status_admin_get_by_id_manufacturer) {
            set_Value(data_admin_get_by_id_manufacturer?.data);
        }
        else {
            var params = {
                id: decodeID(editId)
            };
            dispatch(AdminGetByIdManufacturerAction(params));
        }
    }, [status_admin_get_by_id_manufacturer]);

    useEffect(() => {
        if (status_admin_edit_manufacturer) {
            toast.success(data_admin_edit_manufacturer?.message);
            set_Value(data_admin_edit_manufacturer?.data);
            dispatch(AdminEditManufacturerAction('RESET'));
        }
    }, [status_admin_edit_manufacturer]);
    return (
        <Fragment>
            {loading_admin_get_by_id_manufacturer || loading_admin_edit_manufacturer ? <Spinner /> : ""}
            <div className={`wrapper ${status_show_sidebar ? 'sidebar_minimize nav_open' : ''} ${status_show_sidebar_vertical ? 'topbar_open' : ''}`}>
                <Header />
                <Sidebar />
                <div className="main-panel">
                    <div className="container">
                        <div className="page-inner">
                            <div className="page-header">
                                <h4 className="page-title">Edit Manufacturer</h4>
                                <ul className="breadcrumbs">
                                    <li className="nav-home">
                                        <Link to={REACT_APP_BASE_URL}>
                                            <i className="flaticon-home"></i>
                                        </Link>
                                    </li>
                                    <li className="separator">
                                        <i className="flaticon-right-arrow"></i>
                                    </li>
                                    <li className="nav-item">
                                        <Link to={REACT_APP_BASE_URL + "manufacturer"}>
                                            Manufacturer Management
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                            {status_admin_get_by_id_manufacturer ?
                                <form className="card" onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                                    <div className="p-4">
                                        <div className="form-row">
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Name of manufacturer</label>
                                                <input
                                                    type="text"
                                                    placeholder="Enter Name Of Manufacturer"
                                                    className="form-control"
                                                    minLength={1}
                                                    maxLength={30}
                                                    {...register('name', {
                                                        onChange: (e) => {
                                                            setValue(`name`, e.target.value)
                                                        }
                                                    })}
                                                />
                                                <p className="text-danger">{errors?.name?.message ?? error_admin_edit_manufacturer?.errors?.name?.msg}</p>
                                            </div>
                                            <div className="form-group col-md-6">
                                                <label className="form-label">Hyperlink</label>
                                                <input
                                                    type="url"
                                                    placeholder="Enter Hyperlink"
                                                    className="form-control"
                                                    {...register('hyperlink', {
                                                        onChange: (e) => {
                                                            setValue(`hyperlink`, e.target.value)
                                                        }
                                                    })}
                                                />
                                                <p className="text-danger">{errors?.hyperlink?.message ?? error_admin_edit_manufacturer?.errors?.hyperlink?.msg}</p>
                                            </div>
                                        </div>
                                        <div className="mt-3 text-center">
                                            <button type="submit" className="btn btn-primary">Save</button>
                                        </div>
                                    </div>
                                </form>
                                : ""}
                        </div>
                    </div>
                    <Footer />
                </div>
            </div>
        </Fragment>
    );
}

export default EditManufacturerForm;
